    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate IEnumerator Animate_AfterArgs(Params_Animate args, IEnumerator ret);

        public delegate void Animate_BeforeArgs(Params_Animate args);

        public delegate IEnumerator Animate_WithArgs(Func<HealthCocoon, IEnumerator> orig, HealthCocoon self);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<HealthCocoon> orig, HealthCocoon self);

        public delegate void FlingObjects_AfterArgs(Params_FlingObjects args);

        public delegate void FlingObjects_BeforeArgs(Params_FlingObjects args);

        public delegate void FlingObjects_WithArgs(Action<HealthCocoon, HealthCocoon.FlingPrefab> orig,
            HealthCocoon self, HealthCocoon.FlingPrefab fling);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<HealthCocoon, Collider2D> orig, HealthCocoon self,
            Collider2D collision);

        public delegate void PlaySound_AfterArgs(Params_PlaySound args);

        public delegate void PlaySound_BeforeArgs(Params_PlaySound args);

        public delegate void PlaySound_WithArgs(Action<HealthCocoon, AudioClip> orig, HealthCocoon self,
            AudioClip clip);

        public delegate void SetBroken_AfterArgs(Params_SetBroken args);

        public delegate void SetBroken_BeforeArgs(Params_SetBroken args);

        public delegate void SetBroken_WithArgs(Action<HealthCocoon> orig, HealthCocoon self);

        public delegate void SetScuttlerAmount_AfterArgs(Params_SetScuttlerAmount args);

        public delegate void SetScuttlerAmount_BeforeArgs(Params_SetScuttlerAmount args);

        public delegate void SetScuttlerAmount_WithArgs(Action<HealthCocoon, int> orig, HealthCocoon self, int amount);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<HealthCocoon> orig, HealthCocoon self);

        public sealed class Params_Awake
        {
            public HealthCocoon self;
        }

        public sealed class Params_Start
        {
            public HealthCocoon self;
        }

        public sealed class Params_PlaySound
        {
            public AudioClip clip;
            public HealthCocoon self;
        }

        public sealed class Params_Animate
        {
            public HealthCocoon self;
        }

        public sealed class Params_OnTriggerEnter2D
        {
            public Collider2D collision;
            public HealthCocoon self;
        }

        public sealed class Params_SetBroken
        {
            public HealthCocoon self;
        }

        public sealed class Params_FlingObjects
        {
            public HealthCocoon.FlingPrefab fling;
            public HealthCocoon self;
        }

        public sealed class Params_SetScuttlerAmount
        {
            public int amount;
            public HealthCocoon self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

