        private static bool IsHitTypeBuffered(On.HeroBox.orig_IsHitTypeBuffered orig, int hazardType)
        {
            Delegates.Params_IsHitTypeBuffered @params = new()
            {
                hazardType = hazardType
            };
            if (_beforeIsHitTypeBuffered != null)
            {
                foreach (Delegates.IsHitTypeBuffered_BeforeArgs toInvoke in
                         _beforeIsHitTypeBuffered.GetInvocationList())
                {
                    try
                    {
                        _beforeIsHitTypeBuffered?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            hazardType = @params.hazardType;
            var retVal = orig(hazardType);
            if (_afterIsHitTypeBuffered != null)
            {
                foreach (Delegates.IsHitTypeBuffered_AfterArgs toInvoke in _afterIsHitTypeBuffered.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterIsHitTypeBuffered.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

