    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Start
        {
            public HeroBox self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<HeroBox> orig, HeroBox self);

        public sealed class Params_OnTriggerEnter2D
        {
            public HeroBox self;
            public UnityEngine.Collider2D otherCollider;
        }

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<HeroBox, UnityEngine.Collider2D> orig, HeroBox self,
            UnityEngine.Collider2D otherCollider);

        public sealed class Params_OnTriggerStay2D
        {
            public HeroBox self;
            public UnityEngine.Collider2D otherCollider;
        }

        public delegate void OnTriggerStay2D_BeforeArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_AfterArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_WithArgs(Action<HeroBox, UnityEngine.Collider2D> orig, HeroBox self,
            UnityEngine.Collider2D otherCollider);

        public sealed class Params_CheckForDamage
        {
            public HeroBox self;
            public UnityEngine.Collider2D otherCollider;
        }

        public delegate void CheckForDamage_BeforeArgs(Params_CheckForDamage args);

        public delegate void CheckForDamage_AfterArgs(Params_CheckForDamage args);

        public delegate void CheckForDamage_WithArgs(Action<HeroBox, UnityEngine.Collider2D> orig, HeroBox self,
            UnityEngine.Collider2D otherCollider);

        public sealed class Params_IsHitTypeBuffered
        {
            public int hazardType;
        }

        public delegate void IsHitTypeBuffered_BeforeArgs(Params_IsHitTypeBuffered args);

        public delegate bool IsHitTypeBuffered_AfterArgs(Params_IsHitTypeBuffered args, bool ret);

        public delegate bool IsHitTypeBuffered_WithArgs(Func<int, bool> orig, int hazardType);

        public sealed class Params_LateUpdate
        {
            public HeroBox self;
        }

        public delegate void LateUpdate_BeforeArgs(Params_LateUpdate args);

        public delegate void LateUpdate_AfterArgs(Params_LateUpdate args);

        public delegate void LateUpdate_WithArgs(Action<HeroBox> orig, HeroBox self);

        public sealed class Params_ApplyBufferedHit
        {
            public HeroBox self;
        }

        public delegate void ApplyBufferedHit_BeforeArgs(Params_ApplyBufferedHit args);

        public delegate void ApplyBufferedHit_AfterArgs(Params_ApplyBufferedHit args);

        public delegate void ApplyBufferedHit_WithArgs(Action<HeroBox> orig, HeroBox self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.OnTriggerEnter2D_BeforeArgs OnTriggerEnter2D
        {
            add
