        internal static event Delegates.get_fallTimer_AfterArgs _afterget_fallTimer;

        private static float get_fallTimer(Func<HeroController, float> orig, HeroController self)
        {
            Delegates.Params_get_fallTimer @params = new()
            {
                self = self
            };
            if (_beforeget_fallTimer != null)
                foreach (Delegates.get_fallTimer_BeforeArgs toInvoke in _beforeget_fallTimer.GetInvocationList())
                    try
                    {
                        _beforeget_fallTimer?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_fallTimer != null)
                foreach (Delegates.get_fallTimer_AfterArgs toInvoke in _afterget_fallTimer.GetInvocationList())
                    try
                    {
                        retVal = _afterget_fallTimer.Invoke(@params, retVal);
                    }
                    catch (Exception e)
