        internal static event Delegates.updateSpriteColor_AfterArgs _afterupdateSpriteColor;

        private static void updateSpriteColor(On.InvulnerablePulse.orig_updateSpriteColor orig, InvulnerablePulse self,
            UnityEngine.Color color)
        {
            Delegates.Params_updateSpriteColor @params = new()
            {
                self = self, color = color
            };
            if (_beforeupdateSpriteColor != null)
            {
                foreach (Delegates.updateSpriteColor_BeforeArgs toInvoke in
                         _beforeupdateSpriteColor.GetInvocationList())
                {
