            }

            self = @params.self;
            color = @params.color;
            orig(self, color);
            if (_afterupdateSpriteColor != null)
            {
                foreach (Delegates.updateSpriteColor_AfterArgs toInvoke in _afterupdateSpriteColor.GetInvocationList())
                {
