using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for KeyBindingSourceListener class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnKeyBindingSourceListener
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Reset", true),
                    Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<InControl.KeyBindingSourceListener> orig,
            InControl.KeyBindingSourceListener self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
            {
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                {
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
            {
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                {
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookListen()
        {
            if (!HookedList.Contains("Listen"))
            {
                HookedList.Add("Listen");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Listen", true),
                    Listen);
            }
        }

        internal static event Delegates.Listen_BeforeArgs _beforeListen;
        internal static event Delegates.Listen_AfterArgs _afterListen;

        private static InControl.BindingSource Listen(
            Func<InControl.KeyBindingSourceListener, InControl.BindingListenOptions, InControl.InputDevice,
                InControl.BindingSource> orig, InControl.KeyBindingSourceListener self,
            InControl.BindingListenOptions listenOptions, InControl.InputDevice device)
        {
            Delegates.Params_Listen @params = new()
            {
                self = self, listenOptions = listenOptions, device = device
            };
            if (_beforeListen != null)
            {
                foreach (Delegates.Listen_BeforeArgs toInvoke in _beforeListen.GetInvocationList())
                {
                    try
                    {
                        _beforeListen?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            listenOptions = @params.listenOptions;
            device = @params.device;
            var retVal = orig(self, listenOptions, device);
            if (_afterListen != null)
            {
                foreach (Delegates.Listen_AfterArgs toInvoke in _afterListen.GetInvocationList())
                {
                    try
                    {
                        retVal = _afterListen.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Reset
        {
            public InControl.KeyBindingSourceListener self;
        }

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<InControl.KeyBindingSourceListener> orig,
            InControl.KeyBindingSourceListener self);

        public sealed class Params_Listen
        {
            public InControl.KeyBindingSourceListener self;
            public InControl.BindingListenOptions listenOptions;
            public InControl.InputDevice device;
        }

        public delegate void Listen_BeforeArgs(Params_Listen args);

        public delegate InControl.BindingSource Listen_AfterArgs(Params_Listen args, InControl.BindingSource ret);

        public delegate InControl.BindingSource Listen_WithArgs(
            Func<InControl.KeyBindingSourceListener, InControl.BindingListenOptions, InControl.InputDevice,
                InControl.BindingSource> orig, InControl.KeyBindingSourceListener self,
            InControl.BindingListenOptions listenOptions, InControl.InputDevice device);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.Listen_BeforeArgs Listen
        {
            add
            {
                HookHandler._beforeListen += value;
                HookHandler.HookListen();
            }
            remove => HookHandler._beforeListen -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.Listen_AfterArgs Listen
        {
            add
            {
                HookHandler._afterListen += value;
                HookHandler.HookListen();
            }
            remove => HookHandler._afterListen -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Reset", true), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Reset", true), value);
        }

        public static event Delegates.Listen_WithArgs Listen
        {
            add => HookEndpointManager.Add<Delegates.Listen_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Listen", true), value);
            remove => HookEndpointManager.Remove<Delegates.Listen_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(InControl.KeyBindingSourceListener), "Listen", true), value);
        }
    }
}