namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for Layer class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnLayer
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookInit()
        {
            if (!HookedList.Contains("Init"))
            {
                HookedList.Add("Init");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Layer), "Init"), Init);
            }
        }

        internal static event Delegates.Init_BeforeArgs _beforeInit;
        internal static event Delegates.Init_NormalArgs _afterInit;

        private static void Init(Action<Layer, int, int, int, int, int> orig, Layer self, int hash, int width,
            int height, int divX, int divY)
        {
            Delegates.Params_Init @params = new()
            {
                self = self, hash = hash, width = width, height = height, divX = divX, divY = divY
            };
            _beforeInit?.Invoke(@params);
            self = @params.self;
            hash = @params.hash;
            width = @params.width;
            height = @params.height;
            divX = @params.divX;
            divY = @params.divY;
            orig(self, hash, width, height, divX, divY);
            if (_afterInit != null) _afterInit.Invoke(@params);
        }

