using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;
namespace HKMirror.Hooks.OnHooks;
/// <summary>
/// Contains different types of On Hooks for LinkRendererState class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnLinkRendererState
{
internal static class HookHandler
{
private static List<string> HookedList = new List<string>();
internal static void HookStart()
{
if (!HookedList.Contains("Start"))
{
HookedList.Add("Start");
On.LinkRendererState.Start += Start;

}
}
internal static event Delegates.Start_BeforeArgs _beforeStart;
internal static event Delegates.Start_AfterArgs _afterStart;
private static void Start(On.LinkRendererState.orig_Start orig,LinkRendererState self)
{
Delegates.Params_Start @params = new() 
{
self = self
};
if (_beforeStart != null)
{
foreach (Delegates.Start_BeforeArgs toInvoke in _beforeStart.GetInvocationList())
{
try
{
_beforeStart?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterStart != null)
{
foreach (Delegates.Start_AfterArgs toInvoke in _afterStart.GetInvocationList())
{
try
{
_afterStart.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookUpdate()
{
if (!HookedList.Contains("Update"))
{
HookedList.Add("Update");
On.LinkRendererState.Update += Update;

}
}
internal static event Delegates.Update_BeforeArgs _beforeUpdate;
internal static event Delegates.Update_AfterArgs _afterUpdate;
private static void Update(On.LinkRendererState.orig_Update orig,LinkRendererState self)
{
Delegates.Params_Update @params = new() 
{
self = self
};
if (_beforeUpdate != null)
{
foreach (Delegates.Update_BeforeArgs toInvoke in _beforeUpdate.GetInvocationList())
{
try
{
_beforeUpdate?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterUpdate != null)
{
foreach (Delegates.Update_AfterArgs toInvoke in _afterUpdate.GetInvocationList())
{
try
{
_afterUpdate.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookUpdateLink()
{
if (!HookedList.Contains("UpdateLink"))
{
HookedList.Add("UpdateLink");
On.LinkRendererState.UpdateLink += UpdateLink;

}
}
internal static event Delegates.UpdateLink_BeforeArgs _beforeUpdateLink;
internal static event Delegates.UpdateLink_AfterArgs _afterUpdateLink;
private static void UpdateLink(On.LinkRendererState.orig_UpdateLink orig,LinkRendererState self)
{
Delegates.Params_UpdateLink @params = new() 
{
self = self
};
if (_beforeUpdateLink != null)
{
foreach (Delegates.UpdateLink_BeforeArgs toInvoke in _beforeUpdateLink.GetInvocationList())
{
try
{
_beforeUpdateLink?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterUpdateLink != null)
{
foreach (Delegates.UpdateLink_AfterArgs toInvoke in _afterUpdateLink.GetInvocationList())
{
try
{
_afterUpdateLink.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
}

/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Start
{
public LinkRendererState self;
}
public delegate void Start_BeforeArgs(Params_Start args);
public delegate void Start_AfterArgs(Params_Start args);
public delegate void Start_WithArgs (Action<LinkRendererState> orig, LinkRendererState self);
public sealed class Params_Update
{
public LinkRendererState self;
}
public delegate void Update_BeforeArgs(Params_Update args);
public delegate void Update_AfterArgs(Params_Update args);
public delegate void Update_WithArgs (Action<LinkRendererState> orig, LinkRendererState self);
public sealed class Params_UpdateLink
{
public LinkRendererState self;
}
public delegate void UpdateLink_BeforeArgs(Params_UpdateLink args);
public delegate void UpdateLink_AfterArgs(Params_UpdateLink args);
public delegate void UpdateLink_WithArgs (Action<LinkRendererState> orig, LinkRendererState self);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Start_BeforeArgs Start
{
add
{

HookHandler._beforeStart += value;
HookHandler.HookStart();
}
remove => HookHandler._beforeStart -= value;
}
public static event Delegates.Update_BeforeArgs Update
{
add
{

HookHandler._beforeUpdate += value;
HookHandler.HookUpdate();
}
remove => HookHandler._beforeUpdate -= value;
}
public static event Delegates.UpdateLink_BeforeArgs UpdateLink
{
add
{

HookHandler._beforeUpdateLink += value;
HookHandler.HookUpdateLink();
}
remove => HookHandler._beforeUpdateLink -= value;
}
}

/// <summary>
/// Contains Hooks to that run code after orig(self) is called
/// </summary>
public static class AfterOrig
{
public static event Delegates.Start_AfterArgs Start
{
add
{

HookHandler._afterStart += value;
HookHandler.HookStart();
}
remove => HookHandler._afterStart -= value;
}
public static event Delegates.Update_AfterArgs Update
{
add
{

HookHandler._afterUpdate += value;
HookHandler.HookUpdate();
}
remove => HookHandler._afterUpdate -= value;
}
public static event Delegates.UpdateLink_AfterArgs UpdateLink
{
add
{

HookHandler._afterUpdateLink += value;
HookHandler.HookUpdateLink();
}
remove => HookHandler._afterUpdateLink -= value;
}
}

/// <summary>
/// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
/// </summary>
public static class WithOrig
{
public static event On.LinkRendererState.hook_Start Start
{
add => On.LinkRendererState.Start += value;
remove => On.LinkRendererState.Start -= value;
}
public static event On.LinkRendererState.hook_Update Update
{
add => On.LinkRendererState.Update += value;
remove => On.LinkRendererState.Update -= value;
}
public static event On.LinkRendererState.hook_UpdateLink UpdateLink
{
add => On.LinkRendererState.UpdateLink += value;
remove => On.LinkRendererState.UpdateLink -= value;
}
}

}
