namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for MapMarkerButton class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnMapMarkerButton
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookAwake()
        {
            if (!HookedList.Contains("Awake"))
            {
                HookedList.Add("Awake");
                On.MapMarkerButton.Awake += Awake;
            }
        }

        internal static event Delegates.Awake_BeforeArgs _beforeAwake;
        internal static event Delegates.Awake_NormalArgs _afterAwake;

        private static void Awake(On.MapMarkerButton.orig_Awake orig, MapMarkerButton self)
        {
            Delegates.Params_Awake @params = new()
            {
                self = self
            };
            _beforeAwake?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterAwake != null) _afterAwake.Invoke(@params);
        }

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.MapMarkerButton.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_NormalArgs _afterOnEnable;

        private static void OnEnable(On.MapMarkerButton.orig_OnEnable orig, MapMarkerButton self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            _beforeOnEnable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnable != null) _afterOnEnable.Invoke(@params);
        }

        internal static void HookUpdate()
        {
            if (!HookedList.Contains("Update"))
            {
                HookedList.Add("Update");
                On.MapMarkerButton.Update += Update;
            }
        }

        internal static event Delegates.Update_BeforeArgs _beforeUpdate;
        internal static event Delegates.Update_NormalArgs _afterUpdate;

        private static void Update(On.MapMarkerButton.orig_Update orig, MapMarkerButton self)
        {
            Delegates.Params_Update @params = new()
            {
                self = self
            };
            _beforeUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterUpdate != null) _afterUpdate.Invoke(@params);
        }

        internal static void HookDoDisable()
        {
            if (!HookedList.Contains("DoDisable"))
            {
                HookedList.Add("DoDisable");
                On.MapMarkerButton.DoDisable += DoDisable;
            }
        }

        internal static event Delegates.DoDisable_BeforeArgs _beforeDoDisable;
        internal static event Delegates.DoDisable_NormalArgs _afterDoDisable;

        private static void DoDisable(On.MapMarkerButton.orig_DoDisable orig, MapMarkerButton self)
        {
            Delegates.Params_DoDisable @params = new()
            {
                self = self
            };
            _beforeDoDisable?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoDisable != null) _afterDoDisable.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_NormalArgs(Params_Awake args);

        public delegate void DoDisable_BeforeArgs(Params_DoDisable args);

        public delegate void DoDisable_NormalArgs(Params_DoDisable args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_NormalArgs(Params_OnEnable args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_NormalArgs(Params_Update args);

        public sealed class Params_Awake
        {
            public MapMarkerButton self;
        }

        public sealed class Params_OnEnable
        {
            public MapMarkerButton self;
        }

        public sealed class Params_Update
        {
            public MapMarkerButton self;
        }

        public sealed class Params_DoDisable
        {
            public MapMarkerButton self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
        {
            add
            {
                HookHandler._beforeUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._beforeUpdate -= value;
        }

        public static event Delegates.DoDisable_BeforeArgs DoDisable
        {
            add
            {
                HookHandler._beforeDoDisable += value;
                HookHandler.HookDoDisable();
            }
            remove => HookHandler._beforeDoDisable -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Awake_NormalArgs Awake
        {
            add
            {
                HookHandler._afterAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._afterAwake -= value;
        }

        public static event Delegates.OnEnable_NormalArgs OnEnable
        {
            add
            {
                HookHandler._afterOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._afterOnEnable -= value;
        }

        public static event Delegates.Update_NormalArgs Update
        {
            add
            {
                HookHandler._afterUpdate += value;
                HookHandler.HookUpdate();
            }
            remove => HookHandler._afterUpdate -= value;
        }

        public static event Delegates.DoDisable_NormalArgs DoDisable
        {
            add
            {
                HookHandler._afterDoDisable += value;
                HookHandler.HookDoDisable();
            }
            remove => HookHandler._afterDoDisable -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.MapMarkerButton.hook_Awake Awake
        {
            add => On.MapMarkerButton.Awake += value;
            remove => On.MapMarkerButton.Awake -= value;
        }

        public static event On.MapMarkerButton.hook_OnEnable OnEnable
        {
            add => On.MapMarkerButton.OnEnable += value;
            remove => On.MapMarkerButton.OnEnable -= value;
        }

        public static event On.MapMarkerButton.hook_Update Update
        {
            add => On.MapMarkerButton.Update += value;
            remove => On.MapMarkerButton.Update -= value;
        }

        public static event On.MapMarkerButton.hook_DoDisable DoDisable
        {
            add => On.MapMarkerButton.DoDisable += value;
            remove => On.MapMarkerButton.DoDisable -= value;
        }
    }
}