        private static readonly List<string> HookedList = new();

        internal static void Hookget_invertAxis()
        {
            if (!HookedList.Contains("get_invertAxis"))
            {
                HookedList.Add("get_invertAxis");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Mask), "get_invertAxis"), get_invertAxis);
            }
        }

        internal static event Delegates.get_invertAxis_BeforeArgs _beforeget_invertAxis;
        internal static event Delegates.get_invertAxis_NormalArgs _afterget_invertAxis;

        private static bool get_invertAxis(Func<Mask, bool> orig, Mask self)
        {
            Delegates.Params_get_invertAxis @params = new()
            {
                self = self
            };
            _beforeget_invertAxis?.Invoke(@params);
            self = @params.self;
            var retVal = orig(self);
            if (_afterget_invertAxis != null) retVal = _afterget_invertAxis.Invoke(@params);
            return retVal;
        }

        internal static void Hookset_invertAxis()
        {
            if (!HookedList.Contains("set_invertAxis"))
            {
                HookedList.Add("set_invertAxis");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Mask), "set_invertAxis"), set_invertAxis);
            }
        }

        internal static event Delegates.set_invertAxis_BeforeArgs _beforeset_invertAxis;
        internal static event Delegates.set_invertAxis_NormalArgs _afterset_invertAxis;

        private static void set_invertAxis(Action<Mask, bool> orig, Mask self, bool value)
        {
            Delegates.Params_set_invertAxis @params = new()
            {
                self = self, value = value
            };
            _beforeset_invertAxis?.Invoke(@params);
            self = @params.self;
            value = @params.value;
            orig(self, value);
            if (_afterset_invertAxis != null) _afterset_invertAxis.Invoke(@params);
        }

