        internal static event Delegates.RectTransformToScreenSpace_BeforeArgs _beforeRectTransformToScreenSpace;
        internal static event Delegates.RectTransformToScreenSpace_AfterArgs _afterRectTransformToScreenSpace;

        private static Rect RectTransformToScreenSpace(Func<RectTransform, Rect> orig, RectTransform transform)
        {
            Delegates.Params_RectTransformToScreenSpace @params = new()
            {
                transform = transform
            };
            if (_beforeRectTransformToScreenSpace != null)
                foreach (Delegates.RectTransformToScreenSpace_BeforeArgs toInvoke in _beforeRectTransformToScreenSpace
                             .GetInvocationList())
                    try
                    {
                        _beforeRectTransformToScreenSpace?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            transform = @params.transform;
            var retVal = orig(transform);
            if (_afterRectTransformToScreenSpace != null)
                foreach (Delegates.RectTransformToScreenSpace_AfterArgs toInvoke in _afterRectTransformToScreenSpace
                             .GetInvocationList())
                    try
                    {
                        retVal = _afterRectTransformToScreenSpace.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            return retVal;
        }

