        public delegate void OnPointerClick_BeforeArgs(Params_OnPointerClick args);

        public delegate void OnPointerClick_WithArgs(Action<MenuLanguageSetting, PointerEventData> orig,
            MenuLanguageSetting self, PointerEventData eventData);

