        public delegate void PushUpdateOptionList_BeforeArgs(Params_PushUpdateOptionList args);

        public delegate void PushUpdateOptionList_WithArgs(Action<MenuLanguageSetting> orig, MenuLanguageSetting self);

        public delegate Rect RectTransformToScreenSpace_AfterArgs(Params_RectTransformToScreenSpace args, Rect ret);

        public delegate void RectTransformToScreenSpace_BeforeArgs(Params_RectTransformToScreenSpace args);

        public delegate Rect RectTransformToScreenSpace_WithArgs(Func<RectTransform, Rect> orig,
            RectTransform transform);

        public delegate void RefreshAvailableLanguages_AfterArgs(Params_RefreshAvailableLanguages args);

