using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;
namespace HKMirror.Hooks.OnHooks;
/// <summary>
/// Contains different types of On Hooks for MeshSortingOrder class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnMeshSortingOrder
{
internal static class HookHandler
{
private static List<string> HookedList = new List<string>();
internal static void HookAwake()
{
if (!HookedList.Contains("Awake"))
{
HookedList.Add("Awake");
On.MeshSortingOrder.Awake += Awake;

}
}
internal static event Delegates.Awake_BeforeArgs _beforeAwake;
internal static event Delegates.Awake_AfterArgs _afterAwake;
private static void Awake(On.MeshSortingOrder.orig_Awake orig,MeshSortingOrder self)
{
Delegates.Params_Awake @params = new() 
{
self = self
};
if (_beforeAwake != null)
{
foreach (Delegates.Awake_BeforeArgs toInvoke in _beforeAwake.GetInvocationList())
{
try
{
_beforeAwake?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterAwake != null)
{
foreach (Delegates.Awake_AfterArgs toInvoke in _afterAwake.GetInvocationList())
{
try
{
_afterAwake.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookUpdate()
{
if (!HookedList.Contains("Update"))
{
HookedList.Add("Update");
On.MeshSortingOrder.Update += Update;

}
}
internal static event Delegates.Update_BeforeArgs _beforeUpdate;
internal static event Delegates.Update_AfterArgs _afterUpdate;
private static void Update(On.MeshSortingOrder.orig_Update orig,MeshSortingOrder self)
{
Delegates.Params_Update @params = new() 
{
self = self
};
if (_beforeUpdate != null)
{
foreach (Delegates.Update_BeforeArgs toInvoke in _beforeUpdate.GetInvocationList())
{
try
{
_beforeUpdate?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterUpdate != null)
{
foreach (Delegates.Update_AfterArgs toInvoke in _afterUpdate.GetInvocationList())
{
try
{
_afterUpdate.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
internal static void HookOnValidate()
{
if (!HookedList.Contains("OnValidate"))
{
HookedList.Add("OnValidate");
On.MeshSortingOrder.OnValidate += OnValidate;

}
}
internal static event Delegates.OnValidate_BeforeArgs _beforeOnValidate;
internal static event Delegates.OnValidate_AfterArgs _afterOnValidate;
private static void OnValidate(On.MeshSortingOrder.orig_OnValidate orig,MeshSortingOrder self)
{
Delegates.Params_OnValidate @params = new() 
{
self = self
};
if (_beforeOnValidate != null)
{
foreach (Delegates.OnValidate_BeforeArgs toInvoke in _beforeOnValidate.GetInvocationList())
{
try
{
_beforeOnValidate?.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
self = @params.self;
orig(self);
if (_afterOnValidate != null)
{
foreach (Delegates.OnValidate_AfterArgs toInvoke in _afterOnValidate.GetInvocationList())
{
try
{
_afterOnValidate.Invoke(@params);
}
catch (Exception e) 
{ HKMirrorMod.DoLogError(e);
}
}
}
}
}

/// <summary>
/// Contains necessary information to create Hooks. Does not contain any hooks
/// </summary>
public static class Delegates
{
public sealed class Params_Awake
{
public MeshSortingOrder self;
}
public delegate void Awake_BeforeArgs(Params_Awake args);
public delegate void Awake_AfterArgs(Params_Awake args);
public delegate void Awake_WithArgs (Action<MeshSortingOrder> orig, MeshSortingOrder self);
public sealed class Params_Update
{
public MeshSortingOrder self;
}
public delegate void Update_BeforeArgs(Params_Update args);
public delegate void Update_AfterArgs(Params_Update args);
public delegate void Update_WithArgs (Action<MeshSortingOrder> orig, MeshSortingOrder self);
public sealed class Params_OnValidate
{
public MeshSortingOrder self;
}
public delegate void OnValidate_BeforeArgs(Params_OnValidate args);
public delegate void OnValidate_AfterArgs(Params_OnValidate args);
public delegate void OnValidate_WithArgs (Action<MeshSortingOrder> orig, MeshSortingOrder self);
}

/// <summary>
/// Contains Hooks to that run code before orig(self) is called
/// </summary>
public static class BeforeOrig
{
public static event Delegates.Awake_BeforeArgs Awake
{
add
{

HookHandler._beforeAwake += value;
HookHandler.HookAwake();
}
remove => HookHandler._beforeAwake -= value;
}
public static event Delegates.Update_BeforeArgs Update
{
add
{

HookHandler._beforeUpdate += value;
HookHandler.HookUpdate();
}
remove => HookHandler._beforeUpdate -= value;
}
public static event Delegates.OnValidate_BeforeArgs OnValidate
{
add
{

HookHandler._beforeOnValidate += value;
HookHandler.HookOnValidate();
}
remove => HookHandler._beforeOnValidate -= value;
}
}

/// <summary>
/// Contains Hooks to that run code after orig(self) is called
/// </summary>
public static class AfterOrig
{
public static event Delegates.Awake_AfterArgs Awake
{
add
{

HookHandler._afterAwake += value;
HookHandler.HookAwake();
}
remove => HookHandler._afterAwake -= value;
}
public static event Delegates.Update_AfterArgs Update
{
add
{

HookHandler._afterUpdate += value;
HookHandler.HookUpdate();
}
remove => HookHandler._afterUpdate -= value;
}
public static event Delegates.OnValidate_AfterArgs OnValidate
{
add
{

HookHandler._afterOnValidate += value;
HookHandler.HookOnValidate();
}
remove => HookHandler._afterOnValidate -= value;
}
}

/// <summary>
/// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
/// </summary>
public static class WithOrig
{
public static event On.MeshSortingOrder.hook_Awake Awake
{
add => On.MeshSortingOrder.Awake += value;
remove => On.MeshSortingOrder.Awake -= value;
}
public static event On.MeshSortingOrder.hook_Update Update
{
add => On.MeshSortingOrder.Update += value;
remove => On.MeshSortingOrder.Update -= value;
}
public static event On.MeshSortingOrder.hook_OnValidate OnValidate
{
add => On.MeshSortingOrder.OnValidate += value;
remove => On.MeshSortingOrder.OnValidate -= value;
}
}

}
