    /// </summary>
    public static class Delegates
    {
        public delegate void DoMousePickEvent_AfterArgs(Params_DoMousePickEvent args);

        public delegate void DoMousePickEvent_BeforeArgs(Params_DoMousePickEvent args);

        public delegate void DoMousePickEvent_WithArgs(Action<MousePickEvent> orig, MousePickEvent self);

        public delegate bool DoRaycast_AfterArgs(Params_DoRaycast args, bool ret);

        public delegate void DoRaycast_BeforeArgs(Params_DoRaycast args);

        public delegate bool DoRaycast_WithArgs(Func<MousePickEvent, bool> orig, MousePickEvent self);

        public delegate string ErrorCheck_AfterArgs(Params_ErrorCheck args, string ret);

        public delegate void ErrorCheck_BeforeArgs(Params_ErrorCheck args);

        public delegate string ErrorCheck_WithArgs(Func<MousePickEvent, string> orig, MousePickEvent self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<MousePickEvent> orig, MousePickEvent self);

        public delegate void OnUpdate_AfterArgs(Params_OnUpdate args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_WithArgs(Action<MousePickEvent> orig, MousePickEvent self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<MousePickEvent> orig, MousePickEvent self);

        public sealed class Params_Reset
        {
            public MousePickEvent self;
        }

        public sealed class Params_OnEnter
        {
            public MousePickEvent self;
        }

        public sealed class Params_OnUpdate
        {
            public MousePickEvent self;
        }

        public sealed class Params_DoMousePickEvent
        {
            public MousePickEvent self;
        }

        public sealed class Params_DoRaycast
        {
            public MousePickEvent self;
        }

        public sealed class Params_ErrorCheck
        {
            public MousePickEvent self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
