    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<NailSlash> orig, NailSlash self);

        public delegate void Bounce_AfterArgs(Params_Bounce args);

        public delegate void Bounce_BeforeArgs(Params_Bounce args);

        public delegate void Bounce_WithArgs(Action<NailSlash, Collider2D, bool> orig, NailSlash self,
            Collider2D otherCollider, bool useEffects);

        public delegate void CancelAttack_AfterArgs(Params_CancelAttack args);

        public delegate void CancelAttack_BeforeArgs(Params_CancelAttack args);

        public delegate void CancelAttack_WithArgs(Action<NailSlash> orig, NailSlash self);

        public delegate void Disable_AfterArgs(Params_Disable args);

        public delegate void Disable_BeforeArgs(Params_Disable args);

        public delegate void Disable_WithArgs(Action<NailSlash, tk2dSpriteAnimator, tk2dSpriteAnimationClip> orig,
            NailSlash self, tk2dSpriteAnimator sprite, tk2dSpriteAnimationClip clip);

        public delegate void FixedUpdate_AfterArgs(Params_FixedUpdate args);

        public delegate void FixedUpdate_BeforeArgs(Params_FixedUpdate args);

        public delegate void FixedUpdate_WithArgs(Action<NailSlash> orig, NailSlash self);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<NailSlash, Collider2D> orig, NailSlash self,
            Collider2D otherCollider);

        public delegate void OnTriggerStay2D_AfterArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_BeforeArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_WithArgs(Action<NailSlash, Collider2D> orig, NailSlash self,
            Collider2D otherCollider);

        public delegate void orig_OnTriggerEnter2D_AfterArgs(Params_orig_OnTriggerEnter2D args);

        public delegate void orig_OnTriggerEnter2D_BeforeArgs(Params_orig_OnTriggerEnter2D args);

        public delegate void orig_OnTriggerEnter2D_WithArgs(Action<NailSlash, Collider2D> orig, NailSlash self,
            Collider2D otherCollider);

        public delegate void SetFury_AfterArgs(Params_SetFury args);

        public delegate void SetFury_BeforeArgs(Params_SetFury args);

        public delegate void SetFury_WithArgs(Action<NailSlash, bool> orig, NailSlash self, bool set);

        public delegate void SetLongnail_AfterArgs(Params_SetLongnail args);

        public delegate void SetLongnail_BeforeArgs(Params_SetLongnail args);

        public delegate void SetLongnail_WithArgs(Action<NailSlash, bool> orig, NailSlash self, bool set);

        public delegate void SetMantis_AfterArgs(Params_SetMantis args);

        public delegate void SetMantis_BeforeArgs(Params_SetMantis args);

        public delegate void SetMantis_WithArgs(Action<NailSlash, bool> orig, NailSlash self, bool set);

        public delegate void StartSlash_AfterArgs(Params_StartSlash args);

        public delegate void StartSlash_BeforeArgs(Params_StartSlash args);

        public delegate void StartSlash_WithArgs(Action<NailSlash> orig, NailSlash self);

        public sealed class Params_Awake
        {
            public NailSlash self;
        }

        public sealed class Params_StartSlash
        {
            public NailSlash self;
        }

        public sealed class Params_FixedUpdate
        {
            public NailSlash self;
        }

        public sealed class Params_OnTriggerEnter2D
