    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate bool get_AcceptingInput_AfterArgs(Params_get_AcceptingInput args, bool ret);

        public delegate void get_AcceptingInput_BeforeArgs(Params_get_AcceptingInput args);

        public delegate bool get_AcceptingInput_WithArgs(Func<NextSceneOnInput, bool> orig, NextSceneOnInput self);

        public delegate bool get_ButtonPressed_AfterArgs(Params_get_ButtonPressed args, bool ret);

        public delegate void get_ButtonPressed_BeforeArgs(Params_get_ButtonPressed args);

        public delegate bool get_ButtonPressed_WithArgs(Func<NextSceneOnInput, bool> orig, NextSceneOnInput self);

        public delegate void Skip_AfterArgs(Params_Skip args);

        public delegate void Skip_BeforeArgs(Params_Skip args);

        public delegate void Skip_WithArgs(Action<NextSceneOnInput> orig, NextSceneOnInput self);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<NextSceneOnInput> orig, NextSceneOnInput self);

        public delegate void UnlockSkip_AfterArgs(Params_UnlockSkip args);

        public delegate void UnlockSkip_BeforeArgs(Params_UnlockSkip args);

        public delegate void UnlockSkip_WithArgs(Action<NextSceneOnInput> orig, NextSceneOnInput self);

        public delegate void Update_AfterArgs(Params_Update args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_WithArgs(Action<NextSceneOnInput> orig, NextSceneOnInput self);

        public sealed class Params_get_AcceptingInput
        {
            public NextSceneOnInput self;
        }

        public sealed class Params_get_ButtonPressed
        {
            public NextSceneOnInput self;
        }

        public sealed class Params_Start
        {
            public NextSceneOnInput self;
        }

        public sealed class Params_UnlockSkip
        {
            public NextSceneOnInput self;
        }

        public sealed class Params_Update
        {
            public NextSceneOnInput self;
        }

        public sealed class Params_Skip
        {
            public NextSceneOnInput self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_AcceptingInput_BeforeArgs get_AcceptingInput
        {
            add
            {
                HookHandler._beforeget_AcceptingInput += value;
                HookHandler.Hookget_AcceptingInput();
            }
            remove => HookHandler._beforeget_AcceptingInput -= value;
        }

        public static event Delegates.get_ButtonPressed_BeforeArgs get_ButtonPressed
        {
            add
