    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Start
        {
            public OverscanSetting self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<OverscanSetting> orig, OverscanSetting self);

        public sealed class Params_UpdateValue
        {
            public OverscanSetting self;
        }

        public delegate void UpdateValue_BeforeArgs(Params_UpdateValue args);

        public delegate void UpdateValue_AfterArgs(Params_UpdateValue args);

        public delegate void UpdateValue_WithArgs(Action<OverscanSetting> orig, OverscanSetting self);

        public sealed class Params_UpdateTextValue
        {
            public OverscanSetting self;
            public float value;
        }

        public delegate void UpdateTextValue_BeforeArgs(Params_UpdateTextValue args);

        public delegate void UpdateTextValue_AfterArgs(Params_UpdateTextValue args);

        public delegate void UpdateTextValue_WithArgs(Action<OverscanSetting, float> orig, OverscanSetting self,
            float value);

        public sealed class Params_SetOverscan
        {
            public OverscanSetting self;
            public float value;
        }

        public delegate void SetOverscan_BeforeArgs(Params_SetOverscan args);

        public delegate void SetOverscan_AfterArgs(Params_SetOverscan args);

        public delegate void SetOverscan_WithArgs(Action<OverscanSetting, float> orig, OverscanSetting self,
            float value);

        public sealed class Params_RefreshValueFromSettings
        {
            public OverscanSetting self;
        }

        public delegate void RefreshValueFromSettings_BeforeArgs(Params_RefreshValueFromSettings args);

        public delegate void RefreshValueFromSettings_AfterArgs(Params_RefreshValueFromSettings args);

        public delegate void RefreshValueFromSettings_WithArgs(Action<OverscanSetting> orig, OverscanSetting self);

        public sealed class Params_DoneMode
        {
