        private static void SetValueOnFSM(On.PersistentIntItem.orig_SetValueOnFSM orig, PersistentIntItem self,
            int newValue)
        {
            Delegates.Params_SetValueOnFSM @params = new()
            {
                self = self, newValue = newValue
