            _beforeSetValueOnFSM?.Invoke(@params);
            self = @params.self;
            newValue = @params.newValue;
            orig(self, newValue);
            if (_afterSetValueOnFSM != null) _afterSetValueOnFSM.Invoke(@params);
        }

