using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for PlayAnimation class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnPlayAnimation
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoPlayAnimation()
        {
            if (!HookedList.Contains("DoPlayAnimation"))
            {
                HookedList.Add("DoPlayAnimation");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "DoPlayAnimation"), DoPlayAnimation);
            }
        }

        internal static event Delegates.DoPlayAnimation_BeforeArgs _beforeDoPlayAnimation;
        internal static event Delegates.DoPlayAnimation_NormalArgs _afterDoPlayAnimation;

        private static void DoPlayAnimation(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_DoPlayAnimation @params = new()
            {
                self = self
            };
            _beforeDoPlayAnimation?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoPlayAnimation != null) _afterDoPlayAnimation.Invoke(@params);
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookOnExit()
        {
            if (!HookedList.Contains("OnExit"))
            {
                HookedList.Add("OnExit");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnExit"), OnExit);
            }
        }

        internal static event Delegates.OnExit_BeforeArgs _beforeOnExit;
        internal static event Delegates.OnExit_NormalArgs _afterOnExit;

        private static void OnExit(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_OnExit @params = new()
            {
                self = self
            };
            _beforeOnExit?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnExit != null) _afterOnExit.Invoke(@params);
        }

        internal static void HookStopAnimation()
        {
            if (!HookedList.Contains("StopAnimation"))
            {
                HookedList.Add("StopAnimation");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "StopAnimation"), StopAnimation);
            }
        }

        internal static event Delegates.StopAnimation_BeforeArgs _beforeStopAnimation;
        internal static event Delegates.StopAnimation_NormalArgs _afterStopAnimation;

        private static void StopAnimation(Action<PlayAnimation> orig, PlayAnimation self)
        {
            Delegates.Params_StopAnimation @params = new()
            {
                self = self
            };
            _beforeStopAnimation?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterStopAnimation != null) _afterStopAnimation.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoPlayAnimation_BeforeArgs(Params_DoPlayAnimation args);

        public delegate void DoPlayAnimation_NormalArgs(Params_DoPlayAnimation args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnExit_BeforeArgs(Params_OnExit args);

        public delegate void OnExit_NormalArgs(Params_OnExit args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public delegate void StopAnimation_BeforeArgs(Params_StopAnimation args);

        public delegate void StopAnimation_NormalArgs(Params_StopAnimation args);

        public sealed class Params_Reset
        {
            public PlayAnimation self;
        }

        public sealed class Params_OnEnter
        {
            public PlayAnimation self;
        }

        public sealed class Params_DoPlayAnimation
        {
            public PlayAnimation self;
        }

        public sealed class Params_OnUpdate
        {
            public PlayAnimation self;
        }

        public sealed class Params_OnExit
        {
            public PlayAnimation self;
        }

        public sealed class Params_StopAnimation
        {
            public PlayAnimation self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoPlayAnimation_BeforeArgs DoPlayAnimation
        {
            add
            {
                HookHandler._beforeDoPlayAnimation += value;
                HookHandler.HookDoPlayAnimation();
            }
            remove => HookHandler._beforeDoPlayAnimation -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.OnExit_BeforeArgs OnExit
        {
            add
            {
                HookHandler._beforeOnExit += value;
                HookHandler.HookOnExit();
            }
            remove => HookHandler._beforeOnExit -= value;
        }

        public static event Delegates.StopAnimation_BeforeArgs StopAnimation
        {
            add
            {
                HookHandler._beforeStopAnimation += value;
                HookHandler.HookStopAnimation();
            }
            remove => HookHandler._beforeStopAnimation -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
