        }

        public static event Delegates.DoPlayAnimation_NormalArgs DoPlayAnimation
        {
            add
            {
                HookHandler._afterDoPlayAnimation += value;
                HookHandler.HookDoPlayAnimation();
            }
            remove => HookHandler._afterDoPlayAnimation -= value;
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add
            {
                HookHandler._afterOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._afterOnUpdate -= value;
        }

        public static event Delegates.OnExit_NormalArgs OnExit
        {
            add
            {
                HookHandler._afterOnExit += value;
                HookHandler.HookOnExit();
            }
            remove => HookHandler._afterOnExit -= value;
        }

        public static event Delegates.StopAnimation_NormalArgs StopAnimation
        {
            add
            {
                HookHandler._afterStopAnimation += value;
                HookHandler.HookStopAnimation();
            }
            remove => HookHandler._afterStopAnimation -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnEnter"), value);
        }

        public static event Delegates.DoPlayAnimation_NormalArgs DoPlayAnimation
        {
            add => HookEndpointManager.Add<Delegates.DoPlayAnimation_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "DoPlayAnimation"), value);
            remove => HookEndpointManager.Remove<Delegates.DoPlayAnimation_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "DoPlayAnimation"), value);
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnUpdate"), value);
        }

        public static event Delegates.OnExit_NormalArgs OnExit
        {
            add => HookEndpointManager.Add<Delegates.OnExit_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnExit"), value);
            remove => HookEndpointManager.Remove<Delegates.OnExit_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "OnExit"), value);
        }

        public static event Delegates.StopAnimation_NormalArgs StopAnimation
        {
            add => HookEndpointManager.Add<Delegates.StopAnimation_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "StopAnimation"), value);
            remove => HookEndpointManager.Remove<Delegates.StopAnimation_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(PlayAnimation), "StopAnimation"), value);
        }
    }
}