        }

        internal static void HookDelayStart()
        {
            if (!HookedList.Contains("DelayStart"))
            {
                HookedList.Add("DelayStart");
                On.PlayFromRandomFrameMecanim.DelayStart += DelayStart;
            }
        }

        internal static event Delegates.DelayStart_BeforeArgs _beforeDelayStart;

        private static IEnumerator DelayStart(On.PlayFromRandomFrameMecanim.orig_DelayStart orig,
            PlayFromRandomFrameMecanim self)
        {
            Delegates.Params_DelayStart @params = new()
            {
                self = self
            };
            if (_beforeDelayStart != null)
                foreach (Delegates.DelayStart_BeforeArgs toInvoke in _beforeDelayStart.GetInvocationList())
                    try
                    {
                        _beforeDelayStart?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            return orig(self);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<PlayFromRandomFrameMecanim> orig, PlayFromRandomFrameMecanim self);

        public delegate IEnumerator DelayStart_AfterArgs(Params_DelayStart args, IEnumerator ret);

        public delegate void DelayStart_BeforeArgs(Params_DelayStart args);

        public delegate IEnumerator DelayStart_WithArgs(Func<PlayFromRandomFrameMecanim, IEnumerator> orig,
            PlayFromRandomFrameMecanim self);

        public delegate void DoPlay_AfterArgs(Params_DoPlay args);

        public delegate void DoPlay_BeforeArgs(Params_DoPlay args);

        public delegate void DoPlay_WithArgs(Action<PlayFromRandomFrameMecanim> orig, PlayFromRandomFrameMecanim self);

        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<PlayFromRandomFrameMecanim> orig,
            PlayFromRandomFrameMecanim self);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<PlayFromRandomFrameMecanim> orig, PlayFromRandomFrameMecanim self);

        public sealed class Params_Awake
        {
            public PlayFromRandomFrameMecanim self;
        }

        public sealed class Params_Start
        {
            public PlayFromRandomFrameMecanim self;
        }

        public sealed class Params_OnEnable
        {
            public PlayFromRandomFrameMecanim self;
        }

        public sealed class Params_DoPlay
        {
            public PlayFromRandomFrameMecanim self;
        }

        public sealed class Params_DelayStart
        {
            public PlayFromRandomFrameMecanim self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
