        internal static void Hookget_arrayList()
        {
            if (!HookedList.Contains("get_arrayList"))
            {
                HookedList.Add("get_arrayList");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(PlayMakerArrayListProxy), "get_arrayList"),
                    get_arrayList);
            }
        }

        internal static event Delegates.get_arrayList_BeforeArgs _beforeget_arrayList;
        internal static event Delegates.get_arrayList_AfterArgs _afterget_arrayList;

        private static ArrayList get_arrayList(Func<PlayMakerArrayListProxy, ArrayList> orig,
            PlayMakerArrayListProxy self)
        {
            Delegates.Params_get_arrayList @params = new()
            {
                self = self
            };
            if (_beforeget_arrayList != null)
                foreach (Delegates.get_arrayList_BeforeArgs toInvoke in _beforeget_arrayList.GetInvocationList())
                    try
                    {
                        _beforeget_arrayList?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_arrayList != null)
                foreach (Delegates.get_arrayList_AfterArgs toInvoke in _afterget_arrayList.GetInvocationList())
                    try
                    {
                        retVal = _afterget_arrayList.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            return retVal;
        }

