        public delegate void LatitudeToMercator_BeforeArgs(Params_LatitudeToMercator args);

        public delegate float LatitudeToMercator_WithArgs(Func<float, float> orig, float latitudeInDegrees);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<ProjectLocationToMap> orig, ProjectLocationToMap self);

        public delegate void OnUpdate_AfterArgs(Params_OnUpdate args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_WithArgs(Action<ProjectLocationToMap> orig, ProjectLocationToMap self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ProjectLocationToMap> orig, ProjectLocationToMap self);

