    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoStretch_AfterArgs(Params_DoStretch args);

        public delegate void DoStretch_BeforeArgs(Params_DoStretch args);

        public delegate void DoStretch_WithArgs(Action<ProjectileSquash> orig, ProjectileSquash self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<ProjectileSquash> orig, ProjectileSquash self);

        public delegate void OnFixedUpdate_AfterArgs(Params_OnFixedUpdate args);

        public delegate void OnFixedUpdate_BeforeArgs(Params_OnFixedUpdate args);

        public delegate void OnFixedUpdate_WithArgs(Action<ProjectileSquash> orig, ProjectileSquash self);

        public delegate void OnPreprocess_AfterArgs(Params_OnPreprocess args);

        public delegate void OnPreprocess_BeforeArgs(Params_OnPreprocess args);

        public delegate void OnPreprocess_WithArgs(Action<ProjectileSquash> orig, ProjectileSquash self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ProjectileSquash> orig, ProjectileSquash self);

        public sealed class Params_Reset
        {
            public ProjectileSquash self;
        }

        public sealed class Params_OnPreprocess
        {
            public ProjectileSquash self;
        }

        public sealed class Params_OnEnter
        {
            public ProjectileSquash self;
        }

        public sealed class Params_OnFixedUpdate
        {
            public ProjectileSquash self;
        }

        public sealed class Params_DoStretch
        {
            public ProjectileSquash self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnPreprocess_BeforeArgs OnPreprocess
        {
            add
            {
                HookHandler._beforeOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._beforeOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
