            remove => HookHandler._beforeOnFixedUpdate -= value;
        }

        public static event Delegates.DoStretch_BeforeArgs DoStretch
        {
            add
            {
                HookHandler._beforeDoStretch += value;
                HookHandler.HookDoStretch();
            }
            remove => HookHandler._beforeDoStretch -= value;
        }
    }

