namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for RandomRotationRange class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnRandomRotationRange
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookStart()
        {
            if (!HookedList.Contains("Start"))
            {
                HookedList.Add("Start");
                On.RandomRotationRange.Start += Start;
            }
        }

        internal static event Delegates.Start_BeforeArgs _beforeStart;
        internal static event Delegates.Start_AfterArgs _afterStart;

        private static void Start(On.RandomRotationRange.orig_Start orig, RandomRotationRange self)
        {
            Delegates.Params_Start @params = new()
            {
                self = self
            };
            if (_beforeStart != null)
                foreach (Delegates.Start_BeforeArgs toInvoke in _beforeStart.GetInvocationList())
                    try
                    {
                        _beforeStart?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterStart != null)
                foreach (Delegates.Start_AfterArgs toInvoke in _afterStart.GetInvocationList())
                    try
                    {
                        _afterStart.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnable()
        {
            if (!HookedList.Contains("OnEnable"))
            {
                HookedList.Add("OnEnable");
                On.RandomRotationRange.OnEnable += OnEnable;
            }
        }

        internal static event Delegates.OnEnable_BeforeArgs _beforeOnEnable;
        internal static event Delegates.OnEnable_AfterArgs _afterOnEnable;

        private static void OnEnable(On.RandomRotationRange.orig_OnEnable orig, RandomRotationRange self)
        {
            Delegates.Params_OnEnable @params = new()
            {
                self = self
            };
            if (_beforeOnEnable != null)
                foreach (Delegates.OnEnable_BeforeArgs toInvoke in _beforeOnEnable.GetInvocationList())
                    try
                    {
                        _beforeOnEnable?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterOnEnable != null)
                foreach (Delegates.OnEnable_AfterArgs toInvoke in _afterOnEnable.GetInvocationList())
                    try
                    {
                        _afterOnEnable.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookRandomRotate()
        {
            if (!HookedList.Contains("RandomRotate"))
            {
                HookedList.Add("RandomRotate");
                On.RandomRotationRange.RandomRotate += RandomRotate;
            }
        }

        internal static event Delegates.RandomRotate_BeforeArgs _beforeRandomRotate;
        internal static event Delegates.RandomRotate_AfterArgs _afterRandomRotate;

        private static void RandomRotate(On.RandomRotationRange.orig_RandomRotate orig, RandomRotationRange self)
        {
            Delegates.Params_RandomRotate @params = new()
            {
                self = self
            };
            if (_beforeRandomRotate != null)
                foreach (Delegates.RandomRotate_BeforeArgs toInvoke in _beforeRandomRotate.GetInvocationList())
                    try
                    {
                        _beforeRandomRotate?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterRandomRotate != null)
                foreach (Delegates.RandomRotate_AfterArgs toInvoke in _afterRandomRotate.GetInvocationList())
                    try
                    {
                        _afterRandomRotate.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void OnEnable_AfterArgs(Params_OnEnable args);

        public delegate void OnEnable_BeforeArgs(Params_OnEnable args);

        public delegate void OnEnable_WithArgs(Action<RandomRotationRange> orig, RandomRotationRange self);

        public delegate void RandomRotate_AfterArgs(Params_RandomRotate args);

        public delegate void RandomRotate_BeforeArgs(Params_RandomRotate args);

        public delegate void RandomRotate_WithArgs(Action<RandomRotationRange> orig, RandomRotationRange self);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<RandomRotationRange> orig, RandomRotationRange self);

        public sealed class Params_Start
        {
            public RandomRotationRange self;
        }

        public sealed class Params_OnEnable
        {
            public RandomRotationRange self;
        }

        public sealed class Params_RandomRotate
        {
            public RandomRotationRange self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.OnEnable_BeforeArgs OnEnable
        {
            add
            {
                HookHandler._beforeOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._beforeOnEnable -= value;
        }

        public static event Delegates.RandomRotate_BeforeArgs RandomRotate
        {
            add
            {
                HookHandler._beforeRandomRotate += value;
                HookHandler.HookRandomRotate();
            }
            remove => HookHandler._beforeRandomRotate -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Start_AfterArgs Start
        {
            add
            {
                HookHandler._afterStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._afterStart -= value;
        }

        public static event Delegates.OnEnable_AfterArgs OnEnable
        {
            add
            {
                HookHandler._afterOnEnable += value;
                HookHandler.HookOnEnable();
            }
            remove => HookHandler._afterOnEnable -= value;
        }

        public static event Delegates.RandomRotate_AfterArgs RandomRotate
        {
            add
            {
                HookHandler._afterRandomRotate += value;
                HookHandler.HookRandomRotate();
            }
            remove => HookHandler._afterRandomRotate -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.RandomRotationRange.hook_Start Start
        {
            add => On.RandomRotationRange.Start += value;
            remove => On.RandomRotationRange.Start -= value;
        }

        public static event On.RandomRotationRange.hook_OnEnable OnEnable
        {
            add => On.RandomRotationRange.OnEnable += value;
            remove => On.RandomRotationRange.OnEnable -= value;
        }

        public static event On.RandomRotationRange.hook_RandomRotate RandomRotate
        {
            add => On.RandomRotationRange.RandomRotate += value;
            remove => On.RandomRotationRange.RandomRotate -= value;
        }
    }
}