        internal static void Hookget_SkipFreezingByController()
        {
            if (!HookedList.Contains("get_SkipFreezingByController"))
            {
                HookedList.Add("get_SkipFreezingByController");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(Recoil), "get_SkipFreezingByController", true),
                    get_SkipFreezingByController);
            }
        }

        internal static event Delegates.get_SkipFreezingByController_BeforeArgs _beforeget_SkipFreezingByController;
        internal static event Delegates.get_SkipFreezingByController_AfterArgs _afterget_SkipFreezingByController;

        private static bool get_SkipFreezingByController(Func<Recoil, bool> orig, Recoil self)
        {
            Delegates.Params_get_SkipFreezingByController @params = new()
            {
                self = self
            };
            if (_beforeget_SkipFreezingByController != null)
            {
                foreach (Delegates.get_SkipFreezingByController_BeforeArgs toInvoke in
                         _beforeget_SkipFreezingByController.GetInvocationList())
                {
                    try
                    {
                        _beforeget_SkipFreezingByController?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            var retVal = orig(self);
            if (_afterget_SkipFreezingByController != null)
            {
                foreach (Delegates.get_SkipFreezingByController_AfterArgs toInvoke in _afterget_SkipFreezingByController
                             .GetInvocationList())
                {
                    try
                    {
                        retVal = _afterget_SkipFreezingByController.Invoke(@params, retVal);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            return retVal;
        }

