    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_SkipFreezingByController_BeforeArgs get_SkipFreezingByController
        {
            add
            {
