namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for RemoteDisableChild class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnRemoteDisableChild
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookRemoteDisableObject()
        {
            if (!HookedList.Contains("RemoteDisableObject"))
            {
                HookedList.Add("RemoteDisableObject");
                On.RemoteDisableChild.RemoteDisableObject += RemoteDisableObject;
            }
        }

        internal static event Delegates.RemoteDisableObject_BeforeArgs _beforeRemoteDisableObject;
        internal static event Delegates.RemoteDisableObject_NormalArgs _afterRemoteDisableObject;

        private static void RemoteDisableObject(On.RemoteDisableChild.orig_RemoteDisableObject orig,
            RemoteDisableChild self)
        {
            Delegates.Params_RemoteDisableObject @params = new()
            {
                self = self
            };
            _beforeRemoteDisableObject?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterRemoteDisableObject != null) _afterRemoteDisableObject.Invoke(@params);
        }

        internal static void HookRemoteEnableObject()
        {
            if (!HookedList.Contains("RemoteEnableObject"))
            {
                HookedList.Add("RemoteEnableObject");
                On.RemoteDisableChild.RemoteEnableObject += RemoteEnableObject;
            }
        }

        internal static event Delegates.RemoteEnableObject_BeforeArgs _beforeRemoteEnableObject;
        internal static event Delegates.RemoteEnableObject_NormalArgs _afterRemoteEnableObject;

        private static void RemoteEnableObject(On.RemoteDisableChild.orig_RemoteEnableObject orig,
            RemoteDisableChild self)
        {
            Delegates.Params_RemoteEnableObject @params = new()
            {
                self = self
            };
            _beforeRemoteEnableObject?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterRemoteEnableObject != null) _afterRemoteEnableObject.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void RemoteDisableObject_BeforeArgs(Params_RemoteDisableObject args);

        public delegate void RemoteDisableObject_NormalArgs(Params_RemoteDisableObject args);

        public delegate void RemoteEnableObject_BeforeArgs(Params_RemoteEnableObject args);

        public delegate void RemoteEnableObject_NormalArgs(Params_RemoteEnableObject args);

        public sealed class Params_RemoteDisableObject
        {
            public RemoteDisableChild self;
        }

        public sealed class Params_RemoteEnableObject
        {
            public RemoteDisableChild self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.RemoteDisableObject_BeforeArgs RemoteDisableObject
        {
            add
            {
                HookHandler._beforeRemoteDisableObject += value;
                HookHandler.HookRemoteDisableObject();
            }
            remove => HookHandler._beforeRemoteDisableObject -= value;
        }

        public static event Delegates.RemoteEnableObject_BeforeArgs RemoteEnableObject
        {
            add
            {
                HookHandler._beforeRemoteEnableObject += value;
                HookHandler.HookRemoteEnableObject();
            }
            remove => HookHandler._beforeRemoteEnableObject -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.RemoteDisableObject_NormalArgs RemoteDisableObject
        {
            add
            {
                HookHandler._afterRemoteDisableObject += value;
                HookHandler.HookRemoteDisableObject();
            }
            remove => HookHandler._afterRemoteDisableObject -= value;
        }

        public static event Delegates.RemoteEnableObject_NormalArgs RemoteEnableObject
        {
            add
            {
                HookHandler._afterRemoteEnableObject += value;
                HookHandler.HookRemoteEnableObject();
            }
            remove => HookHandler._afterRemoteEnableObject -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event On.RemoteDisableChild.hook_RemoteDisableObject RemoteDisableObject
        {
            add => On.RemoteDisableChild.RemoteDisableObject += value;
            remove => On.RemoteDisableChild.RemoteDisableObject -= value;
        }

        public static event On.RemoteDisableChild.hook_RemoteEnableObject RemoteEnableObject
        {
            add => On.RemoteDisableChild.RemoteEnableObject += value;
            remove => On.RemoteDisableChild.RemoteEnableObject -= value;
        }
    }
}