{
public sealed class Params_Reset
{
public HutongGames.PlayMaker.Actions.ScaleTime self;
}
public delegate void Reset_BeforeArgs(Params_Reset args);
public delegate void Reset_AfterArgs(Params_Reset args);
public delegate void Reset_WithArgs (Action<HutongGames.PlayMaker.Actions.ScaleTime> orig, HutongGames.PlayMaker.Actions.ScaleTime self);
public sealed class Params_OnEnter
{
public HutongGames.PlayMaker.Actions.ScaleTime self;
}
public delegate void OnEnter_BeforeArgs(Params_OnEnter args);
public delegate void OnEnter_AfterArgs(Params_OnEnter args);
public delegate void OnEnter_WithArgs (Action<HutongGames.PlayMaker.Actions.ScaleTime> orig, HutongGames.PlayMaker.Actions.ScaleTime self);
