public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);
public delegate void OnUpdate_AfterArgs(Params_OnUpdate args);
public delegate void OnUpdate_WithArgs (Action<HutongGames.PlayMaker.Actions.ScaleTime> orig, HutongGames.PlayMaker.Actions.ScaleTime self);
public sealed class Params_DoTimeScale
{
public HutongGames.PlayMaker.Actions.ScaleTime self;
