    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.OnEnable_WithArgs OnEnable
        {
            add => HookEndpointManager.Add<Delegates.OnEnable_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SceneDefaultSettings), "OnEnable"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnable_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SceneDefaultSettings), "OnEnable"), value);
        }

        public static event Delegates.GetMapZoneSettings_WithArgs GetMapZoneSettings
        {
            add => HookEndpointManager.Add<Delegates.GetMapZoneSettings_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SceneDefaultSettings), "GetMapZoneSettings"), value);
