    public static class Delegates
    {
        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<SceneryTriggerCircle> orig, SceneryTriggerCircle self);

        public delegate bool get_active_AfterArgs(Params_get_active args, bool ret);

        public delegate void get_active_BeforeArgs(Params_get_active args);

        public delegate bool get_active_WithArgs(Func<SceneryTriggerCircle, bool> orig, SceneryTriggerCircle self);

        public delegate void OnTriggerEnter2D_AfterArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_BeforeArgs(Params_OnTriggerEnter2D args);

        public delegate void OnTriggerEnter2D_WithArgs(Action<SceneryTriggerCircle, Collider2D> orig,
            SceneryTriggerCircle self, Collider2D other);

        public delegate void OnTriggerExit2D_AfterArgs(Params_OnTriggerExit2D args);

        public delegate void OnTriggerExit2D_BeforeArgs(Params_OnTriggerExit2D args);

        public delegate void OnTriggerExit2D_WithArgs(Action<SceneryTriggerCircle, Collider2D> orig,
            SceneryTriggerCircle self, Collider2D other);

        public delegate void OnTriggerStay2D_AfterArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_BeforeArgs(Params_OnTriggerStay2D args);

        public delegate void OnTriggerStay2D_WithArgs(Action<SceneryTriggerCircle, Collider2D> orig,
            SceneryTriggerCircle self, Collider2D other);

        public delegate void RandomizePitch_AfterArgs(Params_RandomizePitch args);

        public delegate void RandomizePitch_BeforeArgs(Params_RandomizePitch args);

        public delegate void RandomizePitch_WithArgs(Action<SceneryTriggerCircle, AudioSource, float, float> orig,
            SceneryTriggerCircle self, AudioSource src, float minPitch, float maxPitch);

        public delegate void ResetPitch_AfterArgs(Params_ResetPitch args);

        public delegate void ResetPitch_BeforeArgs(Params_ResetPitch args);

        public delegate void ResetPitch_WithArgs(Action<SceneryTriggerCircle, AudioSource> orig,
            SceneryTriggerCircle self, AudioSource src);

        public delegate void set_active_AfterArgs(Params_set_active args);

        public delegate void set_active_BeforeArgs(Params_set_active args);

        public delegate void set_active_WithArgs(Action<SceneryTriggerCircle, bool> orig, SceneryTriggerCircle self,
            bool value);

        public sealed class Params_get_active
        {
            public SceneryTriggerCircle self;
        }

        public sealed class Params_set_active
        {
            public SceneryTriggerCircle self;
            public bool value;
        }

        public sealed class Params_Awake
        {
            public SceneryTriggerCircle self;
        }

        public sealed class Params_OnTriggerEnter2D
