        }

        public sealed class Params_RandomizePitch
        {
            public float maxPitch;
            public float minPitch;
            public SceneryTriggerCircle self;
            public AudioSource src;
        }

        public sealed class Params_ResetPitch
        {
            public SceneryTriggerCircle self;
            public AudioSource src;
        }
    }

    /// <summary>
