    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.get_active_BeforeArgs get_active
        {
            add
            {
                HookHandler._beforeget_active += value;
                HookHandler.Hookget_active();
            }
            remove => HookHandler._beforeget_active -= value;
        }

