    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<ScuttlerControl> orig, ScuttlerControl self);

        public delegate IEnumerator Bounce_AfterArgs(Params_Bounce args, IEnumerator ret);

        public delegate void Bounce_BeforeArgs(Params_Bounce args);

        public delegate IEnumerator Bounce_WithArgs(Func<ScuttlerControl, float, float, IEnumerator> orig,
            ScuttlerControl self, float angleMin, float angleMax);

        public delegate IEnumerator Heal_AfterArgs(Params_Heal args, IEnumerator ret);

        public delegate void Heal_BeforeArgs(Params_Heal args);

        public delegate IEnumerator Heal_WithArgs(Func<ScuttlerControl, IEnumerator> orig, ScuttlerControl self);

        public delegate void Hit_AfterArgs(Params_Hit args);

        public delegate void Hit_BeforeArgs(Params_Hit args);

        public delegate void Hit_WithArgs(Action<ScuttlerControl, HitInstance> orig, ScuttlerControl self,
            HitInstance damageInstance);

        public delegate IEnumerator Land_AfterArgs(Params_Land args, IEnumerator ret);

        public delegate void Land_BeforeArgs(Params_Land args);

        public delegate IEnumerator Land_WithArgs(Func<ScuttlerControl, IEnumerator> orig, ScuttlerControl self);

        public delegate IEnumerator Run_AfterArgs(Params_Run args, IEnumerator ret);

        public delegate void Run_BeforeArgs(Params_Run args);

        public delegate IEnumerator Run_WithArgs(Func<ScuttlerControl, IEnumerator> orig, ScuttlerControl self);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<ScuttlerControl> orig, ScuttlerControl self);

        public delegate void Update_AfterArgs(Params_Update args);

        public delegate void Update_BeforeArgs(Params_Update args);

        public delegate void Update_WithArgs(Action<ScuttlerControl> orig, ScuttlerControl self);

        public sealed class Params_Awake
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Start
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Update
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Land
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Run
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Bounce
        {
            public float angleMax;
            public float angleMin;
            public ScuttlerControl self;
        }

        public sealed class Params_Heal
        {
            public ScuttlerControl self;
        }

        public sealed class Params_Hit
        {
            public HitInstance damageInstance;
            public ScuttlerControl self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
                HookHandler.HookStart();
            }
            remove => HookHandler._beforeStart -= value;
        }

        public static event Delegates.Update_BeforeArgs Update
