    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.get_playID_NormalArgs get_playID
        {
            add => HookEndpointManager.Add<Delegates.get_playID_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SecureplayerPrefsDemoClass), "get_playID"), value);
            remove => HookEndpointManager.Remove<Delegates.get_playID_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SecureplayerPrefsDemoClass), "get_playID"), value);
        }

        public static event Delegates.set_playID_NormalArgs set_playID
        {
