using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for SendEnemyMessage class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSendEnemyMessage
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "Reset",
                        true), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<HutongGames.PlayMaker.Actions.SendEnemyMessage> orig,
            HutongGames.PlayMaker.Actions.SendEnemyMessage self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
            {
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                {
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
            {
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                {
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "OnEnter",
                        true), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<HutongGames.PlayMaker.Actions.SendEnemyMessage> orig,
            HutongGames.PlayMaker.Actions.SendEnemyMessage self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
            {
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                {
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
            {
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                {
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookSendWalkerGoInDirection()
        {
            if (!HookedList.Contains("SendWalkerGoInDirection"))
            {
                HookedList.Add("SendWalkerGoInDirection");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage),
                        "SendWalkerGoInDirection", false), SendWalkerGoInDirection);
            }
        }

        internal static event Delegates.SendWalkerGoInDirection_BeforeArgs _beforeSendWalkerGoInDirection;
        internal static event Delegates.SendWalkerGoInDirection_AfterArgs _afterSendWalkerGoInDirection;

        private static void SendWalkerGoInDirection(Action<UnityEngine.GameObject, int> orig,
            UnityEngine.GameObject target, int facing)
        {
            Delegates.Params_SendWalkerGoInDirection @params = new()
            {
                target = target, facing = facing
            };
            if (_beforeSendWalkerGoInDirection != null)
            {
                foreach (Delegates.SendWalkerGoInDirection_BeforeArgs toInvoke in _beforeSendWalkerGoInDirection
                             .GetInvocationList())
                {
                    try
                    {
                        _beforeSendWalkerGoInDirection?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            target = @params.target;
            facing = @params.facing;
            orig(target, facing);
            if (_afterSendWalkerGoInDirection != null)
            {
                foreach (Delegates.SendWalkerGoInDirection_AfterArgs toInvoke in _afterSendWalkerGoInDirection
                             .GetInvocationList())
                {
                    try
                    {
                        _afterSendWalkerGoInDirection.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Reset
        {
            public HutongGames.PlayMaker.Actions.SendEnemyMessage self;
        }

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<HutongGames.PlayMaker.Actions.SendEnemyMessage> orig,
            HutongGames.PlayMaker.Actions.SendEnemyMessage self);

        public sealed class Params_OnEnter
        {
            public HutongGames.PlayMaker.Actions.SendEnemyMessage self;
        }

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<HutongGames.PlayMaker.Actions.SendEnemyMessage> orig,
            HutongGames.PlayMaker.Actions.SendEnemyMessage self);

        public sealed class Params_SendWalkerGoInDirection
        {
            public UnityEngine.GameObject target;
            public int facing;
        }

        public delegate void SendWalkerGoInDirection_BeforeArgs(Params_SendWalkerGoInDirection args);

        public delegate void SendWalkerGoInDirection_AfterArgs(Params_SendWalkerGoInDirection args);

        public delegate void SendWalkerGoInDirection_WithArgs(Action<UnityEngine.GameObject, int> orig,
            UnityEngine.GameObject target, int facing);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.SendWalkerGoInDirection_BeforeArgs SendWalkerGoInDirection
        {
            add
            {
                HookHandler._beforeSendWalkerGoInDirection += value;
                HookHandler.HookSendWalkerGoInDirection();
            }
            remove => HookHandler._beforeSendWalkerGoInDirection -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.SendWalkerGoInDirection_AfterArgs SendWalkerGoInDirection
        {
            add
            {
                HookHandler._afterSendWalkerGoInDirection += value;
                HookHandler.HookSendWalkerGoInDirection();
            }
            remove => HookHandler._afterSendWalkerGoInDirection -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "Reset", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "Reset", true),
                value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "OnEnter", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage), "OnEnter", true),
                value);
        }

        public static event Delegates.SendWalkerGoInDirection_WithArgs SendWalkerGoInDirection
        {
            add => HookEndpointManager.Add<Delegates.SendWalkerGoInDirection_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage),
                    "SendWalkerGoInDirection", false), value);
            remove => HookEndpointManager.Remove<Delegates.SendWalkerGoInDirection_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SendEnemyMessage),
                    "SendWalkerGoInDirection", false), value);
        }
    }
}