using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for SetAnimatorIKGoal class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSetAnimatorIKGoal
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<SetAnimatorIKGoal> orig, SetAnimatorIKGoal self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnPreprocess()
        {
            if (!HookedList.Contains("OnPreprocess"))
            {
                HookedList.Add("OnPreprocess");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnPreprocess"), OnPreprocess);
            }
        }

        internal static event Delegates.OnPreprocess_BeforeArgs _beforeOnPreprocess;
        internal static event Delegates.OnPreprocess_NormalArgs _afterOnPreprocess;

        private static void OnPreprocess(Action<SetAnimatorIKGoal> orig, SetAnimatorIKGoal self)
        {
            Delegates.Params_OnPreprocess @params = new()
            {
                self = self
            };
            _beforeOnPreprocess?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnPreprocess != null) _afterOnPreprocess.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<SetAnimatorIKGoal> orig, SetAnimatorIKGoal self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoAnimatorIK()
        {
            if (!HookedList.Contains("DoAnimatorIK"))
            {
                HookedList.Add("DoAnimatorIK");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoAnimatorIK"), DoAnimatorIK);
            }
        }

        internal static event Delegates.DoAnimatorIK_BeforeArgs _beforeDoAnimatorIK;
        internal static event Delegates.DoAnimatorIK_NormalArgs _afterDoAnimatorIK;

        private static void DoAnimatorIK(Action<SetAnimatorIKGoal, int> orig, SetAnimatorIKGoal self, int layerIndex)
        {
            Delegates.Params_DoAnimatorIK @params = new()
            {
                self = self, layerIndex = layerIndex
            };
            _beforeDoAnimatorIK?.Invoke(@params);
            self = @params.self;
            layerIndex = @params.layerIndex;
            orig(self, layerIndex);
            if (_afterDoAnimatorIK != null) _afterDoAnimatorIK.Invoke(@params);
        }

        internal static void HookDoSetIKGoal()
        {
            if (!HookedList.Contains("DoSetIKGoal"))
            {
                HookedList.Add("DoSetIKGoal");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoSetIKGoal"), DoSetIKGoal);
            }
        }

        internal static event Delegates.DoSetIKGoal_BeforeArgs _beforeDoSetIKGoal;
        internal static event Delegates.DoSetIKGoal_NormalArgs _afterDoSetIKGoal;

        private static void DoSetIKGoal(Action<SetAnimatorIKGoal> orig, SetAnimatorIKGoal self)
        {
            Delegates.Params_DoSetIKGoal @params = new()
            {
                self = self
            };
            _beforeDoSetIKGoal?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoSetIKGoal != null) _afterDoSetIKGoal.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoAnimatorIK_BeforeArgs(Params_DoAnimatorIK args);

        public delegate void DoAnimatorIK_NormalArgs(Params_DoAnimatorIK args);

        public delegate void DoSetIKGoal_BeforeArgs(Params_DoSetIKGoal args);

        public delegate void DoSetIKGoal_NormalArgs(Params_DoSetIKGoal args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnPreprocess_BeforeArgs(Params_OnPreprocess args);

        public delegate void OnPreprocess_NormalArgs(Params_OnPreprocess args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public SetAnimatorIKGoal self;
        }

        public sealed class Params_OnPreprocess
        {
            public SetAnimatorIKGoal self;
        }

        public sealed class Params_OnEnter
        {
            public SetAnimatorIKGoal self;
        }

        public sealed class Params_DoAnimatorIK
        {
            public int layerIndex;
            public SetAnimatorIKGoal self;
        }

        public sealed class Params_DoSetIKGoal
        {
            public SetAnimatorIKGoal self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnPreprocess_BeforeArgs OnPreprocess
        {
            add
            {
                HookHandler._beforeOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._beforeOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoAnimatorIK_BeforeArgs DoAnimatorIK
        {
            add
            {
                HookHandler._beforeDoAnimatorIK += value;
                HookHandler.HookDoAnimatorIK();
            }
            remove => HookHandler._beforeDoAnimatorIK -= value;
        }

        public static event Delegates.DoSetIKGoal_BeforeArgs DoSetIKGoal
        {
            add
            {
                HookHandler._beforeDoSetIKGoal += value;
                HookHandler.HookDoSetIKGoal();
            }
            remove => HookHandler._beforeDoSetIKGoal -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnPreprocess_NormalArgs OnPreprocess
        {
            add
            {
                HookHandler._afterOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._afterOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoAnimatorIK_NormalArgs DoAnimatorIK
        {
            add
            {
                HookHandler._afterDoAnimatorIK += value;
                HookHandler.HookDoAnimatorIK();
            }
            remove => HookHandler._afterDoAnimatorIK -= value;
        }

        public static event Delegates.DoSetIKGoal_NormalArgs DoSetIKGoal
        {
            add
            {
                HookHandler._afterDoSetIKGoal += value;
                HookHandler.HookDoSetIKGoal();
            }
            remove => HookHandler._afterDoSetIKGoal -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "Reset"), value);
        }

        public static event Delegates.OnPreprocess_NormalArgs OnPreprocess
        {
            add => HookEndpointManager.Add<Delegates.OnPreprocess_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnPreprocess"), value);
            remove => HookEndpointManager.Remove<Delegates.OnPreprocess_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnPreprocess"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "OnEnter"), value);
        }

        public static event Delegates.DoAnimatorIK_NormalArgs DoAnimatorIK
        {
            add => HookEndpointManager.Add<Delegates.DoAnimatorIK_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoAnimatorIK"), value);
            remove => HookEndpointManager.Remove<Delegates.DoAnimatorIK_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoAnimatorIK"), value);
        }

        public static event Delegates.DoSetIKGoal_NormalArgs DoSetIKGoal
        {
            add => HookEndpointManager.Add<Delegates.DoSetIKGoal_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoSetIKGoal"), value);
            remove => HookEndpointManager.Remove<Delegates.DoSetIKGoal_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorIKGoal), "DoSetIKGoal"), value);
        }
    }
}