        internal static event Delegates.DoPlaybackSpeed_BeforeArgs _beforeDoPlaybackSpeed;
        internal static event Delegates.DoPlaybackSpeed_AfterArgs _afterDoPlaybackSpeed;

        private static void DoPlaybackSpeed(Action<HutongGames.PlayMaker.Actions.SetAnimatorSpeed> orig,
