using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for SetAnimatorTarget class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSetAnimatorTarget
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<SetAnimatorTarget> orig, SetAnimatorTarget self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnPreprocess()
        {
            if (!HookedList.Contains("OnPreprocess"))
            {
                HookedList.Add("OnPreprocess");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnPreprocess"), OnPreprocess);
            }
        }

        internal static event Delegates.OnPreprocess_BeforeArgs _beforeOnPreprocess;
        internal static event Delegates.OnPreprocess_NormalArgs _afterOnPreprocess;

        private static void OnPreprocess(Action<SetAnimatorTarget> orig, SetAnimatorTarget self)
        {
            Delegates.Params_OnPreprocess @params = new()
            {
                self = self
            };
            _beforeOnPreprocess?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnPreprocess != null) _afterOnPreprocess.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<SetAnimatorTarget> orig, SetAnimatorTarget self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookDoAnimatorMove()
        {
            if (!HookedList.Contains("DoAnimatorMove"))
            {
                HookedList.Add("DoAnimatorMove");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "DoAnimatorMove"), DoAnimatorMove);
            }
        }

        internal static event Delegates.DoAnimatorMove_BeforeArgs _beforeDoAnimatorMove;
        internal static event Delegates.DoAnimatorMove_NormalArgs _afterDoAnimatorMove;

        private static void DoAnimatorMove(Action<SetAnimatorTarget> orig, SetAnimatorTarget self)
        {
            Delegates.Params_DoAnimatorMove @params = new()
            {
                self = self
            };
            _beforeDoAnimatorMove?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoAnimatorMove != null) _afterDoAnimatorMove.Invoke(@params);
        }

        internal static void HookSetTarget()
        {
            if (!HookedList.Contains("SetTarget"))
            {
                HookedList.Add("SetTarget");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "SetTarget"), SetTarget);
            }
        }

        internal static event Delegates.SetTarget_BeforeArgs _beforeSetTarget;
        internal static event Delegates.SetTarget_NormalArgs _afterSetTarget;

        private static void SetTarget(Action<SetAnimatorTarget> orig, SetAnimatorTarget self)
        {
            Delegates.Params_SetTarget @params = new()
            {
                self = self
            };
            _beforeSetTarget?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterSetTarget != null) _afterSetTarget.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoAnimatorMove_BeforeArgs(Params_DoAnimatorMove args);

        public delegate void DoAnimatorMove_NormalArgs(Params_DoAnimatorMove args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnPreprocess_BeforeArgs(Params_OnPreprocess args);

        public delegate void OnPreprocess_NormalArgs(Params_OnPreprocess args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public delegate void SetTarget_BeforeArgs(Params_SetTarget args);

        public delegate void SetTarget_NormalArgs(Params_SetTarget args);

        public sealed class Params_Reset
        {
            public SetAnimatorTarget self;
        }

        public sealed class Params_OnPreprocess
        {
            public SetAnimatorTarget self;
        }

        public sealed class Params_OnEnter
        {
            public SetAnimatorTarget self;
        }

        public sealed class Params_DoAnimatorMove
        {
            public SetAnimatorTarget self;
        }

        public sealed class Params_SetTarget
        {
            public SetAnimatorTarget self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnPreprocess_BeforeArgs OnPreprocess
        {
            add
            {
                HookHandler._beforeOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._beforeOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoAnimatorMove_BeforeArgs DoAnimatorMove
        {
            add
            {
                HookHandler._beforeDoAnimatorMove += value;
                HookHandler.HookDoAnimatorMove();
            }
            remove => HookHandler._beforeDoAnimatorMove -= value;
        }

        public static event Delegates.SetTarget_BeforeArgs SetTarget
        {
            add
            {
                HookHandler._beforeSetTarget += value;
                HookHandler.HookSetTarget();
            }
            remove => HookHandler._beforeSetTarget -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnPreprocess_NormalArgs OnPreprocess
        {
            add
            {
                HookHandler._afterOnPreprocess += value;
                HookHandler.HookOnPreprocess();
            }
            remove => HookHandler._afterOnPreprocess -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoAnimatorMove_NormalArgs DoAnimatorMove
        {
            add
            {
                HookHandler._afterDoAnimatorMove += value;
                HookHandler.HookDoAnimatorMove();
            }
            remove => HookHandler._afterDoAnimatorMove -= value;
        }

        public static event Delegates.SetTarget_NormalArgs SetTarget
        {
            add
            {
                HookHandler._afterSetTarget += value;
                HookHandler.HookSetTarget();
            }
            remove => HookHandler._afterSetTarget -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "Reset"), value);
        }

        public static event Delegates.OnPreprocess_NormalArgs OnPreprocess
        {
            add => HookEndpointManager.Add<Delegates.OnPreprocess_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnPreprocess"), value);
            remove => HookEndpointManager.Remove<Delegates.OnPreprocess_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnPreprocess"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "OnEnter"), value);
        }

        public static event Delegates.DoAnimatorMove_NormalArgs DoAnimatorMove
        {
            add => HookEndpointManager.Add<Delegates.DoAnimatorMove_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "DoAnimatorMove"), value);
            remove => HookEndpointManager.Remove<Delegates.DoAnimatorMove_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "DoAnimatorMove"), value);
        }

        public static event Delegates.SetTarget_NormalArgs SetTarget
        {
            add => HookEndpointManager.Add<Delegates.SetTarget_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "SetTarget"), value);
            remove => HookEndpointManager.Remove<Delegates.SetTarget_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetAnimatorTarget), "SetTarget"), value);
        }
    }
}