        public static event Delegates.SetTarget_BeforeArgs SetTarget
        {
            add
            {
                HookHandler._beforeSetTarget += value;
                HookHandler.HookSetTarget();
            }
            remove => HookHandler._beforeSetTarget -= value;
        }
    }

