using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
/// Contains different types of On Hooks for SetLightCookie class.<br />
/// Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSetLightCookie
{
    internal static class HookHandler
    {
        private static List<string> HookedList = new List<string>();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "Reset", true),
                    Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
            {
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                {
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
            {
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                {
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "OnEnter",
                        true), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
            {
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                {
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
            {
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                {
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }

        internal static void HookDoSetLightCookie()
        {
            if (!HookedList.Contains("DoSetLightCookie"))
            {
                HookedList.Add("DoSetLightCookie");
                new Hook(
                    ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie),
                        "DoSetLightCookie", true), DoSetLightCookie);
            }
        }

        internal static event Delegates.DoSetLightCookie_BeforeArgs _beforeDoSetLightCookie;
        internal static event Delegates.DoSetLightCookie_AfterArgs _afterDoSetLightCookie;

        private static void DoSetLightCookie(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self)
        {
            Delegates.Params_DoSetLightCookie @params = new()
            {
                self = self
            };
            if (_beforeDoSetLightCookie != null)
            {
                foreach (Delegates.DoSetLightCookie_BeforeArgs toInvoke in _beforeDoSetLightCookie.GetInvocationList())
                {
                    try
                    {
                        _beforeDoSetLightCookie?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }

            self = @params.self;
            orig(self);
            if (_afterDoSetLightCookie != null)
            {
                foreach (Delegates.DoSetLightCookie_AfterArgs toInvoke in _afterDoSetLightCookie.GetInvocationList())
                {
                    try
                    {
                        _afterDoSetLightCookie.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
                }
            }
        }
    }

    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Reset
        {
            public HutongGames.PlayMaker.Actions.SetLightCookie self;
        }

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self);

        public sealed class Params_OnEnter
        {
            public HutongGames.PlayMaker.Actions.SetLightCookie self;
        }

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self);

        public sealed class Params_DoSetLightCookie
        {
            public HutongGames.PlayMaker.Actions.SetLightCookie self;
        }

        public delegate void DoSetLightCookie_BeforeArgs(Params_DoSetLightCookie args);

        public delegate void DoSetLightCookie_AfterArgs(Params_DoSetLightCookie args);

        public delegate void DoSetLightCookie_WithArgs(Action<HutongGames.PlayMaker.Actions.SetLightCookie> orig,
            HutongGames.PlayMaker.Actions.SetLightCookie self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoSetLightCookie_BeforeArgs DoSetLightCookie
        {
            add
            {
                HookHandler._beforeDoSetLightCookie += value;
                HookHandler.HookDoSetLightCookie();
            }
            remove => HookHandler._beforeDoSetLightCookie -= value;
        }
    }

    /// <summary>
    /// Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoSetLightCookie_AfterArgs DoSetLightCookie
        {
            add
            {
                HookHandler._afterDoSetLightCookie += value;
                HookHandler.HookDoSetLightCookie();
            }
            remove => HookHandler._afterDoSetLightCookie -= value;
        }
    }

    /// <summary>
    /// Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "Reset", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "Reset", true),
                value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "OnEnter", true),
                value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "OnEnter", true),
                value);
        }

        public static event Delegates.DoSetLightCookie_WithArgs DoSetLightCookie
        {
            add => HookEndpointManager.Add<Delegates.DoSetLightCookie_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "DoSetLightCookie",
                    true), value);
            remove => HookEndpointManager.Remove<Delegates.DoSetLightCookie_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(HutongGames.PlayMaker.Actions.SetLightCookie), "DoSetLightCookie",
                    true), value);
        }
    }
}