using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for SetMaterialColor class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSetMaterialColor
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_NormalArgs _afterReset;

        private static void Reset(Action<SetMaterialColor> orig, SetMaterialColor self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            _beforeReset?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterReset != null) _afterReset.Invoke(@params);
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_NormalArgs _afterOnEnter;

        private static void OnEnter(Action<SetMaterialColor> orig, SetMaterialColor self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            _beforeOnEnter?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnEnter != null) _afterOnEnter.Invoke(@params);
        }

        internal static void HookGetNameId()
        {
            if (!HookedList.Contains("GetNameId"))
            {
                HookedList.Add("GetNameId");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "GetNameId", false), GetNameId);
            }
        }

        internal static event Delegates.GetNameId_BeforeArgs _beforeGetNameId;
        internal static event Delegates.GetNameId_NormalArgs _afterGetNameId;

        private static int GetNameId(Func<FsmString, int> orig, FsmString fsmStr)
        {
            Delegates.Params_GetNameId @params = new()
            {
                fsmStr = fsmStr
            };
            _beforeGetNameId?.Invoke(@params);
            fsmStr = @params.fsmStr;
            var retVal = orig(fsmStr);
            if (_afterGetNameId != null) retVal = _afterGetNameId.Invoke(@params);
            return retVal;
        }

        internal static void HookOnUpdate()
        {
            if (!HookedList.Contains("OnUpdate"))
            {
                HookedList.Add("OnUpdate");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnUpdate"), OnUpdate);
            }
        }

        internal static event Delegates.OnUpdate_BeforeArgs _beforeOnUpdate;
        internal static event Delegates.OnUpdate_NormalArgs _afterOnUpdate;

        private static void OnUpdate(Action<SetMaterialColor> orig, SetMaterialColor self)
        {
            Delegates.Params_OnUpdate @params = new()
            {
                self = self
            };
            _beforeOnUpdate?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterOnUpdate != null) _afterOnUpdate.Invoke(@params);
        }

        internal static void HookDoSetMaterialColor()
        {
            if (!HookedList.Contains("DoSetMaterialColor"))
            {
                HookedList.Add("DoSetMaterialColor");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "DoSetMaterialColor"),
                    DoSetMaterialColor);
            }
        }

        internal static event Delegates.DoSetMaterialColor_BeforeArgs _beforeDoSetMaterialColor;
        internal static event Delegates.DoSetMaterialColor_NormalArgs _afterDoSetMaterialColor;

        private static void DoSetMaterialColor(Action<SetMaterialColor> orig, SetMaterialColor self)
        {
            Delegates.Params_DoSetMaterialColor @params = new()
            {
                self = self
            };
            _beforeDoSetMaterialColor?.Invoke(@params);
            self = @params.self;
            orig(self);
            if (_afterDoSetMaterialColor != null) _afterDoSetMaterialColor.Invoke(@params);
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoSetMaterialColor_BeforeArgs(Params_DoSetMaterialColor args);

        public delegate void DoSetMaterialColor_NormalArgs(Params_DoSetMaterialColor args);

        public delegate void GetNameId_BeforeArgs(Params_GetNameId args);

        public delegate int GetNameId_NormalArgs(Params_GetNameId args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_NormalArgs(Params_OnEnter args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_NormalArgs(Params_OnUpdate args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_NormalArgs(Params_Reset args);

        public sealed class Params_Reset
        {
            public SetMaterialColor self;
        }

        public sealed class Params_OnEnter
        {
            public SetMaterialColor self;
        }

        public sealed class Params_GetNameId
        {
            public FsmString fsmStr;
        }

        public sealed class Params_OnUpdate
        {
            public SetMaterialColor self;
        }

        public sealed class Params_DoSetMaterialColor
        {
            public SetMaterialColor self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.GetNameId_BeforeArgs GetNameId
        {
            add
            {
                HookHandler._beforeGetNameId += value;
                HookHandler.HookGetNameId();
            }
            remove => HookHandler._beforeGetNameId -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._beforeOnUpdate -= value;
        }

        public static event Delegates.DoSetMaterialColor_BeforeArgs DoSetMaterialColor
        {
            add
            {
                HookHandler._beforeDoSetMaterialColor += value;
                HookHandler.HookDoSetMaterialColor();
            }
            remove => HookHandler._beforeDoSetMaterialColor -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.GetNameId_NormalArgs GetNameId
        {
            add
            {
                HookHandler._afterGetNameId += value;
                HookHandler.HookGetNameId();
            }
            remove => HookHandler._afterGetNameId -= value;
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add
            {
                HookHandler._afterOnUpdate += value;
                HookHandler.HookOnUpdate();
            }
            remove => HookHandler._afterOnUpdate -= value;
        }

        public static event Delegates.DoSetMaterialColor_NormalArgs DoSetMaterialColor
        {
            add
            {
                HookHandler._afterDoSetMaterialColor += value;
                HookHandler.HookDoSetMaterialColor();
            }
            remove => HookHandler._afterDoSetMaterialColor -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_NormalArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "Reset"), value);
        }

        public static event Delegates.OnEnter_NormalArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnEnter"), value);
        }

        public static event Delegates.GetNameId_NormalArgs GetNameId
        {
            add => HookEndpointManager.Add<Delegates.GetNameId_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "GetNameId", false), value);
            remove => HookEndpointManager.Remove<Delegates.GetNameId_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "GetNameId", false), value);
        }

        public static event Delegates.OnUpdate_NormalArgs OnUpdate
        {
            add => HookEndpointManager.Add<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnUpdate"), value);
            remove => HookEndpointManager.Remove<Delegates.OnUpdate_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "OnUpdate"), value);
        }

        public static event Delegates.DoSetMaterialColor_NormalArgs DoSetMaterialColor
        {
            add => HookEndpointManager.Add<Delegates.DoSetMaterialColor_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "DoSetMaterialColor"), value);
            remove => HookEndpointManager.Remove<Delegates.DoSetMaterialColor_NormalArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetMaterialColor), "DoSetMaterialColor"), value);
        }
    }
}