using MonoMod.RuntimeDetour;
using MonoMod.RuntimeDetour.HookGen;

namespace HKMirror.Hooks.OnHooks;

/// <summary>
///     Contains different types of On Hooks for SetRandomMaterial class.<br />
///     Contains hooks that aren't generated by monomod, and before and after orig hooks for easier hooking.
/// </summary>
public static class OnSetRandomMaterial
{
    internal static class HookHandler
    {
        private static readonly List<string> HookedList = new();

        internal static void HookReset()
        {
            if (!HookedList.Contains("Reset"))
            {
                HookedList.Add("Reset");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "Reset"), Reset);
            }
        }

        internal static event Delegates.Reset_BeforeArgs _beforeReset;
        internal static event Delegates.Reset_AfterArgs _afterReset;

        private static void Reset(Action<SetRandomMaterial> orig, SetRandomMaterial self)
        {
            Delegates.Params_Reset @params = new()
            {
                self = self
            };
            if (_beforeReset != null)
                foreach (Delegates.Reset_BeforeArgs toInvoke in _beforeReset.GetInvocationList())
                    try
                    {
                        _beforeReset?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterReset != null)
                foreach (Delegates.Reset_AfterArgs toInvoke in _afterReset.GetInvocationList())
                    try
                    {
                        _afterReset.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookOnEnter()
        {
            if (!HookedList.Contains("OnEnter"))
            {
                HookedList.Add("OnEnter");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "OnEnter"), OnEnter);
            }
        }

        internal static event Delegates.OnEnter_BeforeArgs _beforeOnEnter;
        internal static event Delegates.OnEnter_AfterArgs _afterOnEnter;

        private static void OnEnter(Action<SetRandomMaterial> orig, SetRandomMaterial self)
        {
            Delegates.Params_OnEnter @params = new()
            {
                self = self
            };
            if (_beforeOnEnter != null)
                foreach (Delegates.OnEnter_BeforeArgs toInvoke in _beforeOnEnter.GetInvocationList())
                    try
                    {
                        _beforeOnEnter?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterOnEnter != null)
                foreach (Delegates.OnEnter_AfterArgs toInvoke in _afterOnEnter.GetInvocationList())
                    try
                    {
                        _afterOnEnter.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }

        internal static void HookDoSetRandomMaterial()
        {
            if (!HookedList.Contains("DoSetRandomMaterial"))
            {
                HookedList.Add("DoSetRandomMaterial");
                new Hook(ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "DoSetRandomMaterial"),
                    DoSetRandomMaterial);
            }
        }

        internal static event Delegates.DoSetRandomMaterial_BeforeArgs _beforeDoSetRandomMaterial;
        internal static event Delegates.DoSetRandomMaterial_AfterArgs _afterDoSetRandomMaterial;

        private static void DoSetRandomMaterial(Action<SetRandomMaterial> orig, SetRandomMaterial self)
        {
            Delegates.Params_DoSetRandomMaterial @params = new()
            {
                self = self
            };
            if (_beforeDoSetRandomMaterial != null)
                foreach (Delegates.DoSetRandomMaterial_BeforeArgs toInvoke in _beforeDoSetRandomMaterial
                             .GetInvocationList())
                    try
                    {
                        _beforeDoSetRandomMaterial?.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }

            self = @params.self;
            orig(self);
            if (_afterDoSetRandomMaterial != null)
                foreach (Delegates.DoSetRandomMaterial_AfterArgs toInvoke in
                         _afterDoSetRandomMaterial.GetInvocationList())
                    try
                    {
                        _afterDoSetRandomMaterial.Invoke(@params);
                    }
                    catch (Exception e)
                    {
                        HKMirrorMod.DoLogError(e);
                    }
        }
    }

    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void DoSetRandomMaterial_AfterArgs(Params_DoSetRandomMaterial args);

        public delegate void DoSetRandomMaterial_BeforeArgs(Params_DoSetRandomMaterial args);

        public delegate void DoSetRandomMaterial_WithArgs(Action<SetRandomMaterial> orig, SetRandomMaterial self);

        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<SetRandomMaterial> orig, SetRandomMaterial self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<SetRandomMaterial> orig, SetRandomMaterial self);

        public sealed class Params_Reset
        {
            public SetRandomMaterial self;
        }

        public sealed class Params_OnEnter
        {
            public SetRandomMaterial self;
        }

        public sealed class Params_DoSetRandomMaterial
        {
            public SetRandomMaterial self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.DoSetRandomMaterial_BeforeArgs DoSetRandomMaterial
        {
            add
            {
                HookHandler._beforeDoSetRandomMaterial += value;
                HookHandler.HookDoSetRandomMaterial();
            }
            remove => HookHandler._beforeDoSetRandomMaterial -= value;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code after orig(self) is called
    /// </summary>
    public static class AfterOrig
    {
        public static event Delegates.Reset_AfterArgs Reset
        {
            add
            {
                HookHandler._afterReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._afterReset -= value;
        }

        public static event Delegates.OnEnter_AfterArgs OnEnter
        {
            add
            {
                HookHandler._afterOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._afterOnEnter -= value;
        }

        public static event Delegates.DoSetRandomMaterial_AfterArgs DoSetRandomMaterial
        {
            add
            {
                HookHandler._afterDoSetRandomMaterial += value;
                HookHandler.HookDoSetRandomMaterial();
            }
            remove => HookHandler._afterDoSetRandomMaterial -= value;
        }
    }

    /// <summary>
    ///     Contains all On Hooks, even those that aren't in the On namespace such as API generated functions and property
    ///     getters/setters
    /// </summary>
    public static class WithOrig
    {
        public static event Delegates.Reset_WithArgs Reset
        {
            add => HookEndpointManager.Add<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "Reset"), value);
            remove => HookEndpointManager.Remove<Delegates.Reset_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "Reset"), value);
        }

        public static event Delegates.OnEnter_WithArgs OnEnter
        {
            add => HookEndpointManager.Add<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "OnEnter"), value);
            remove => HookEndpointManager.Remove<Delegates.OnEnter_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "OnEnter"), value);
        }

        public static event Delegates.DoSetRandomMaterial_WithArgs DoSetRandomMaterial
        {
            add => HookEndpointManager.Add<Delegates.DoSetRandomMaterial_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "DoSetRandomMaterial"), value);
            remove => HookEndpointManager.Remove<Delegates.DoSetRandomMaterial_WithArgs>(
                ReflectionHelper.GetMethodInfo(typeof(SetRandomMaterial), "DoSetRandomMaterial"), value);
        }
    }
}