    /// <summary>
    /// Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public sealed class Params_Awake
        {
            public ShadowGateColliderControl self;
        }

        public delegate void Awake_BeforeArgs(Params_Awake args);

        public delegate void Awake_AfterArgs(Params_Awake args);

        public delegate void Awake_WithArgs(Action<ShadowGateColliderControl> orig, ShadowGateColliderControl self);

        public sealed class Params_Start
        {
            public ShadowGateColliderControl self;
        }

        public delegate void Start_BeforeArgs(Params_Start args);

        public delegate void Start_AfterArgs(Params_Start args);

        public delegate void Start_WithArgs(Action<ShadowGateColliderControl> orig, ShadowGateColliderControl self);

        public sealed class Params_OnDestroy
        {
            public ShadowGateColliderControl self;
        }

        public delegate void OnDestroy_BeforeArgs(Params_OnDestroy args);

        public delegate void OnDestroy_AfterArgs(Params_OnDestroy args);

        public delegate void OnDestroy_WithArgs(Action<ShadowGateColliderControl> orig, ShadowGateColliderControl self);

        public sealed class Params_Setup
        {
            public ShadowGateColliderControl self;
        }

        public delegate void Setup_BeforeArgs(Params_Setup args);

        public delegate void Setup_AfterArgs(Params_Setup args);

        public delegate void Setup_WithArgs(Action<ShadowGateColliderControl> orig, ShadowGateColliderControl self);

        public sealed class Params_FixedUpdate
        {
            public ShadowGateColliderControl self;
        }

        public delegate void FixedUpdate_BeforeArgs(Params_FixedUpdate args);

        public delegate void FixedUpdate_AfterArgs(Params_FixedUpdate args);

        public delegate void FixedUpdate_WithArgs(Action<ShadowGateColliderControl> orig,
            ShadowGateColliderControl self);
    }

    /// <summary>
    /// Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Awake_BeforeArgs Awake
        {
            add
            {
                HookHandler._beforeAwake += value;
                HookHandler.HookAwake();
            }
            remove => HookHandler._beforeAwake -= value;
        }

        public static event Delegates.Start_BeforeArgs Start
        {
            add
            {
                HookHandler._beforeStart += value;
