    /// <summary>
    ///     Contains necessary information to create Hooks. Does not contain any hooks
    /// </summary>
    public static class Delegates
    {
        public delegate void OnEnter_AfterArgs(Params_OnEnter args);

        public delegate void OnEnter_BeforeArgs(Params_OnEnter args);

        public delegate void OnEnter_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public delegate void OnExit_AfterArgs(Params_OnExit args);

        public delegate void OnExit_BeforeArgs(Params_OnExit args);

        public delegate void OnExit_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public delegate void OnUpdate_AfterArgs(Params_OnUpdate args);

        public delegate void OnUpdate_BeforeArgs(Params_OnUpdate args);

        public delegate void OnUpdate_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public delegate void Reset_AfterArgs(Params_Reset args);

        public delegate void Reset_BeforeArgs(Params_Reset args);

        public delegate void Reset_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public delegate void StopAndReset_AfterArgs(Params_StopAndReset args);

        public delegate void StopAndReset_BeforeArgs(Params_StopAndReset args);

        public delegate void StopAndReset_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public delegate void UpdateShaking_AfterArgs(Params_UpdateShaking args);

        public delegate void UpdateShaking_BeforeArgs(Params_UpdateShaking args);

        public delegate void UpdateShaking_WithArgs(Action<ShakePosition> orig, ShakePosition self);

        public sealed class Params_Reset
        {
            public ShakePosition self;
        }

        public sealed class Params_OnEnter
        {
            public ShakePosition self;
        }

        public sealed class Params_OnUpdate
        {
            public ShakePosition self;
        }

        public sealed class Params_OnExit
        {
            public ShakePosition self;
        }

        public sealed class Params_UpdateShaking
        {
            public ShakePosition self;
        }

        public sealed class Params_StopAndReset
        {
            public ShakePosition self;
        }
    }

    /// <summary>
    ///     Contains Hooks to that run code before orig(self) is called
    /// </summary>
    public static class BeforeOrig
    {
        public static event Delegates.Reset_BeforeArgs Reset
        {
            add
            {
                HookHandler._beforeReset += value;
                HookHandler.HookReset();
            }
            remove => HookHandler._beforeReset -= value;
        }

        public static event Delegates.OnEnter_BeforeArgs OnEnter
        {
            add
            {
                HookHandler._beforeOnEnter += value;
                HookHandler.HookOnEnter();
            }
            remove => HookHandler._beforeOnEnter -= value;
        }

        public static event Delegates.OnUpdate_BeforeArgs OnUpdate
        {
            add
            {
                HookHandler._beforeOnUpdate += value;
